import { Component } from "react";
import { Helpers, SimpleGrid } from "core";
import Constants from '../utils/constants';
import { GetUrl, HandleError } from '../utils/generals'
import { Backward } from "../utils/generals";

export default class RequestListSection extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {};

        this.showMessage = this.showMessage.bind(this);
        this.closeRequest = this.closeRequest.bind(this);
        this.getMoreInfoLinks = this.getMoreInfoLinks.bind(this);
        this.openStrLink = this.openStrLink.bind(this);
    }   

    // Events.
    downloadFile(record, url)
    {
        if(record == null || record.id == null || record.id == "")
        {
          Helpers.DisplayErrorMessage(this.props.notificationSystem, null, 'شناسه درخواست پیدا نشد');
          return;
        }
        var wm = window.open(GetUrl(`${url}/${record.id}`), "_target");

        wm.focus();
        wm.onblur = function() { wm.close(); };
    }

    showHideMessage(item)
    {
        return item.errorMessage != null && item.errorMessage != "";
    }

    showMessage(item)
    {
        Helpers.DisplayErrorMessage(this.props.notificationSystem, null, item.errorMessage);
    }

    closeRequest(item)
    {
        if(item == null || item.id == null || item.id == "")
        {
            Helpers.DisplayErrorMessage(this.props.notificationSystem, null, "شناسه درخواست جهت مختومه کردن پیدا نشد");
            return;
        }
        this.setState({ isLoading: true })
        Helpers.CallServer('/api/amls/Request/CloseRequest/' + item.id,
        { },
        successfulResponse =>
        {
            Helpers.DisplaySuccessMessage(this.props.notificationSystem, successfulResponse.message);
        },
        errorResponse => HandleError(Helpers, this.props.notificationSystem, errorResponse),
        () => { this.setState({ isLoading: false }) }, 'GET');
    }

    hasDownloadFile(record)
    {
        return record.statusId == Constants.RequestState.Done.Key && record.requestTypeId != Constants.RequestType.Registering_STR;
    }

    isRequestDone(record)
    {
        return record.statusId == Constants.RequestState.Done.Key;
    }

    getMoreInfoLinks()
    {
        var menu = [];
        
        switch(this.props.requestTypeId)
        {
            case Constants.RequestType.Billing:
                menu.push({ title:'صورتحساب شخص', icon:'receipt-2', enable: this.hasDownloadFile, url: GetUrl('personBillingListPage/#replaceWith_id#') });
                menu.push({ title:'دانلود فایل صورتحساب', icon:'receipt-2', enable: this.hasDownloadFile, func: (rd) => this.downloadFile(rd, 'downloadBillings'), url: "" });
                menu.push({ title:'ثبت درخواست STR', icon:'license', url: "", func: this.openStrLink });
            break;
            case Constants.RequestType.Roles:
                menu.push({ title:'دانلود فایل قواعد', icon:'file-spreadsheet', enable: this.hasDownloadFile, func: (rd) => this.downloadFile(rd, 'downloadIndex'), url: "" });
            break;
            case Constants.RequestType.Registering_STR:
                menu.push({ title:'صورتحساب 68 ستونی مشتری', icon:'receipt-2', enable: this.isRequestDone, url: GetUrl('registerStrPersonBillings/#replaceWith_id#') });
                menu.push({ title:'نمایش STR', icon:'license', enable: this.isRequestDone, url: GetUrl('strListPage/#replaceWith_id#') });
                menu.push({ title:'نمایش لاگ', icon:'note', enable: this.isRequestDone, url: GetUrl('registerStrLogListPage/#replaceWith_id#') });
            break;
            case Constants.RequestType.DownloadReport_STR:
                menu.push({ title:'دانلود گزارش STR', icon:'file-spreadsheet', enable: this.hasDownloadFile, func: (rd) => this.downloadFile(rd, 'Download_STR_Report'), url: "" });
            break;
            case Constants.RequestType.DownloadReport_CTR:
                menu.push({ title:'دانلود گزارش CTR', icon:'file-spreadsheet', enable: this.hasDownloadFile, func: (rd) => this.downloadFile(rd, 'Download_CTR_Report'), url: "" });
            break;
        }
        menu.push({ title:'مختومه', icon:'row-remove', url: "", enable: this.hasDownloadFile, func: this.closeRequest });
        menu.push({ title:'پیام سیستم', icon:'message', func: this.showMessage, url: "", divider: true, enable: this.showHideMessage });

        return menu;
    }

    openStrLink(record)
    {
        var url = "";

        // 
        if(record.nationalCode != null && record.nationalCode != "")
            url = GetUrl(`registerStrAddPage/${0}/${record.nationalCode}/${Constants.PersonNumberType.Legal.value}`);
        else if(record.ownerAccountNationalCode != null && record.ownerAccountNationalCode != "")
            url = GetUrl(`registerStrAddPage/${0}/${record.ownerAccountNationalCode}/${Constants.PersonNumberType.Real.value}`);

        if(url == "")
        {
            Helpers.DisplayErrorMessage(this.notificationSystem, null, "اطلاعات فرد پیدا نشد");
            return;
        }
        window.open(url, "_target");
    }

    render()
    {
        return (
            <>
                <div className="card">
                    <div className="card-body">
                        <SimpleGrid context={ this } minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                            filterBgColor="primary" name="crdrqstList" title={ this.props.title } notificationSystem={ this.props.notificationSystem } enableAutoFetch
                            listApiUrl="/api/amls/Request/SearchRequests" extraItemsSentToFetchData={{ RequestTypeId: this.props.requestTypeId }}
                            moreInfoLinks={ this.getMoreInfoLinks() }
                            headers={[
                                        { title: "کد پرسنل", name: "personCode" },
                                        { title: "نام پرسنل", name: "personName" },
                                        { title: "تاریخ درخواست", name: "requestDateStr" },
                                        { title: "نوع درخواست", name: "requestTypeName" },
                                        { title: "وضعیت", name: "statusName" },
                                        { title: "کد خطا", name: "errorTrackingCode" },
                                ]}
                                badgeOnCells={[
                                    {
                                        fieldName: "statusName",
                                        color: (x) => {
                                        switch (x) {
                                            case Constants.RequestState.Registerd.Title:
                                                return "gray"
                                            case Constants.RequestState.InProcess.Title:
                                                return "yellow";
                                            case Constants.RequestState.Done.Title:
                                                return "green";
                                            case Constants.RequestState.Error.Title:
                                                return "red";
                                        }}}]}
                            filterOptions={[
                                    { type: 'auto-complete', name: 'personCode', title: 'پرسنل', lookupApiUrl: "/api/amls/DistributionOfIndicator/SearchPersonels" },
                                    { type: "datepicker", name: "startDate", title: "از تاریخ", useEpoch: true },
                                    { type: "datepicker", name: "endDate", title: "تا تاریخ", useEpoch: true }
                            ]} />
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => Backward(e) } href="/" title="بازگشت به صفحه قبل">
                                بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}
