import React, { Component } from 'react';
import { PageMain } from "core";
import FacilityTypeSection from '../../sections/basePage/facilityTypeSection';
import { Helpers } from "core";
import { HandleError } from '../../utils/generals'

class FacilityTypeEditPage extends React.Component {
    constructor(props) {
        super(props);
        this.itemId = this.props.itemId;
        this.isNew = this.itemId == null;
        this.state = { isLoading: true };
        this.notificationSystem = React.createRef();

    }
    componentDidMount()
    {
      
            if (!this.isNew) {
                const _this = this;
                Helpers.CallServer(
                  "/api/FRQST/FacilityType/Post",
                  
                  (res) => {
                    _this.setState({ item: res });
                  },
                  (errorResponse) => HandleError(Helpers, this.notificationSystem, errorResponse),
                  () => _this.setState({ isLoading: false }),
                  
                );
              }
       }
    render() {
        return (
          
 
          <PageMain isLoading={ this.state.isLoading } title=" فرم" icon="users" context={this} footerLinks={[{ href: "/", text: "صفحه اصلی" },{ href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" }]}>
                <FacilityTypeSection item={this.state}/>
          </PageMain>

        );
    }
}


export default FacilityTypeEditPage
