import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError, GetDropDownValues } from "../../utils/generals";
import Constants from "../../utils/constants";
import { Backward } from "../../utils/generals";
import { reportsUnder18yearOptions } from "../../utils/selectOptionsUtils";
export default class ReportsUnder18yearPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
    };

    this.notificationSystem = React.createRef();
    this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
  }

  onDeleteErrorFunc(errorResponse) {
    return HandleError(Helpers, this.notificationSystem, errorResponse);
  }

  render() {
    return (
      <>
        <PageMain
          context={this}
          title="گزارشهای زیر 18 سال"
          icon="list-search"
          isLoading={this.state.isLoading}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
        >
          <div className="card">
            <div className="card-body">
              <SimpleGrid
                context={this}
                dataKey="id"
                minHeightTableWrapper={Constants.MinGridHeight}
                hasSearchBox={false}
                hideExportButton
                filterBgColor="primary"
                name="reportsUnder18yearList"
                title="گزارش زیر 18 سال"
                notificationSystem={this.notificationSystem}
                enableAutoFetch
                listApiUrl="/api/amls/"
                deleteApiUrl="/api/amls/"
                onDeleteErrorFunc={this.onDeleteErrorFunc}
                headers={[{ title: "ردیف", name: "name" }]}
                filterOptions={[
                  {
                    type: "select",
                    name: "reportId",
                    title: "نوع گزارش",
                    selectOptions: reportsUnder18yearOptions(),
                    // className: "form-required",
                    //required,
                  },
                  {
                    type: "text",
                    name: "customerNumber",
                    title: "شماره مشتری",
                  },
                  { type: "text", name: "natinalCode", title: "کدملی" },
                  { type: "text", name: "accountNumber", title: "شماره حساب" },
                  {
                    type: "datepicker",
                    name: "fromAccountNumberDate",
                    title: "تاریخ افتتاح حساب از ",
                    useEpoch: true,
                  },
                  {
                    type: "datepicker",
                    name: "endAccountNumberDate",
                    title: "تاریخ افتتاح حساب تا ",
                    useEpoch: true,
                  },
                ]}
              />
            </div>
            <div className="card-footer">
              <div className="d-flex">
                <a
                  className="btn btn-outline-secendery ms-auto"
                  onClick={(e) => Backward(e)}
                  href="/"
                  title="بازگشت به صفحه قبل"
                >
                  بازگشت
                  <i className="ti ti-arrow-left ms-1"></i>
                </a>
              </div>
            </div>
          </div>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
