import React from "react";
import { TextBox, DatePicker, Helpers, DropDown, FileUploader, ImageUploader, AutoComplete } from "core";
import Constants from '../../utils/constants'

export default class 
DynamicControlSection extends React.Component
{
    constructor(props)
    {
        super(props);
    }

    // Functions.
    GetValidationType(id, minLength, maxLength)
    {
        var validationHandler = [];

        switch(id)
        {
            case Constants.ValidationTypes.Text_Min_Max:
                validationHandler.push(Helpers.MinMaxLengthValidator);
                break;
            case Constants.ValidationTypes.Number:
                validationHandler.push(Helpers.NumberTypeValidator);
                break;
            case Constants.ValidationTypes.MobileNumber:
                validationHandler.push(Helpers.RegexValidator);
                break;
            case Constants.ValidationTypes.FloatNumber:
                validationHandler.push(Helpers.FloatNumberTypeValidator);
                break;
        }
        
        if(id != Constants.ValidationTypes.Text_Min_Max)
        {
            if(minLength != null && maxLength != null)
                validationHandler.push(Helpers.MinMaxLengthValidator);
            else if(minLength != null)
                validationHandler.push(Helpers.MinLengthValidator);
            else if(maxLength != null)
                validationHandler.push(Helpers.MaxLengthValidator);
        }

        return (validationHandler.length == 0 ? null : validationHandler);
    }

    GetRex = (id) => {
        switch(id)
        {
            case Constants.ValidationTypes.MobileNumber:
                return Helpers.MobileRegexPattern;
            default:
                return null;
        }
    }

    // Events.
    OnAutoCompleteItemSelected(fieldId, item)
    {
        this.props.onAutoCompleteItemSelected(fieldId, item);
    }

    render()
    {
        switch(this.props.data.typeId)
        {
            case Constants.FieldTypes.TextBox:
                return <TextBox context={ this.props.parentContext }
                                name={ this.props.data.fieldId }
                                title={ this.props.data.name }
                                placeholder={ this.props.data.fieldTypeInfo.placeHolder }
                                isLTR={ this.props.data.fieldTypeInfo.isLTR }
                                required={ this.props.data.isRequired }
                                readOnly={ this.props.data.fieldTypeInfo.readOnly }
                                multiline={ this.props.data.fieldTypeInfo.isMultiLine }
                                hint={ this.props.data.fieldTypeInfo.hint }
                                commaSeparated={ this.props.data.fieldTypeInfo.isMoney }
                                minLength={ this.props.data.fieldTypeInfo.minLength }
                                maxLength={ this.props.data.fieldTypeInfo.maxLength }
                                validateHandler={ this.GetValidationType(this.props.data.fieldTypeInfo.validationTypeId, this.props.data.fieldTypeInfo.minLength, this.props.data.fieldTypeInfo.maxLength) }
                                regex={ this.GetRex(this.props.data.fieldTypeInfo.validationTypeId) } />;
            case Constants.FieldTypes.DatePicker:
                return <DatePicker context={ this.props.parentContext }
                                   name={ this.props.data.fieldId }
                                   title={ this.props.data.name }
                                   useEpoch={ this.props.data.fieldTypeInfo.useEpoch }
                                   isGregorian={ false }
                                   timePicker={ this.props.data.fieldTypeInfo.hasTime }
                                   required={ this.props.data.isRequired } />;
            case Constants.FieldTypes.DropDown:
                return <DropDown context={ this.props.parentContext }
                                 name={ this.props.data.fieldId }
                                 title={ this.props.data.name }
                                 options={ this.props.data.fieldTypeInfo.items }
                                 required={ this.props.data.isRequired } />;
            case Constants.FieldTypes.FileUploader:
                return <FileUploader context={ this.props.parentContext }
                                     name={ this.props.data.fieldId }
                                     title={ this.props.data.name }
                                     required={ this.props.data.isRequired }
                                     validExtensions={ this.props.data.fieldTypeInfo.supportedFileExtensions } />;
            case Constants.FieldTypes.ImageUploader:
                return <ImageUploader context={ this.props.parentContext }
                                      name={ this.props.data.fieldId }
                                      title={ this.props.data.name }
                                      required={ this.props.data.isRequired }
                                      validExtensions={ this.props.data.fieldTypeInfo.supportedFileExtensions } />;
            case Constants.FieldTypes.CitySelect:
                return <AutoComplete context={ this.props.parentContext }
                                      name={ this.props.data.fieldId }
                                      title={ this.props.data.name }
                                      required={ this.props.data.isRequired }
                                      notificationSystem={ this.props.parentNotificationSystem }
                                      remoteUrl={ this.props.data.fieldTypeInfo.autoCompleteUrl }
                                      changeHandler={ (item) => this.OnAutoCompleteItemSelected(this.props.data.fieldId, item) }
                                      externalData={ this.props.offLineAutoCompleteList == null ? [] : this.props.offLineAutoCompleteList } />;
        }
    }
}
