import React from "react";
import { Helpers, PageMain, SimpleGrid } from "core";
import NotificationSystem from 'react-notification-system'
import { GetUrl, HandleError } from '../../utils/generals'

export default class FormListPage extends React.Component
{
    // Constructor
    constructor(props)
    {
        super(props);
        this.notificationSystem = React.createRef();

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

    onDeleteErrorFunc(errorResponse)
    {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
    }

    render()
    {
        return (<>
                    <PageMain
                        title="فهرست فرم های پویا"
                        icon="list-details"
                        context={ this }
                        footerLinks={[ { href: "/", text: "صفحه اصلی" }, { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" } ]}>
                            <SimpleGrid context={ this }
                                name="grid"
                                title="جزئیات فهرست فرم های پویا"
                                enableAutoFetch
                                listApiUrl='/api/FRQST/Form/GetFormList'
                                disabledPaging
                                dataKey="id"
                                headers={[
                                    { title: "نام فرم", name: "name" },
                                    { title: "چند فرمی", name: "isMulti", type: "bool" },
                                    { title: "اجباری", name: "isRequired", type: "bool" },
                                    { title: "ترتیب نمایش", name: "order" }
                                        ]}
                                editPageUrl={ GetUrl('formEditPage') }
                                deleteModalFieldNames={["name"]}
                                deleteApiUrl='/api/FRQST/Form/DeleteForm'
                                onDeleteErrorFunc={ this.onDeleteErrorFunc }
                                moreInfoLinks={[
                                    { title:'ساخت فرم پویا', icon:'forms', url: GetUrl('fieldsSavePage/#replaceWith_id#') },
                                    { title:'ایجاد متغییر ارجاع', icon:'variable', url: GetUrl('formVariableRefferenceDataPage/#replaceWith_id#') },
                                    { title:'ایجاد فرمول', icon:'a-b', url: GetUrl('formFormulaPage/#replaceWith_id#') }
                                ]}
                                hideExportButton
                                minHeightTableWrapper={ 200 }
                                notificationSystem={ this.notificationSystem } />
                    </PageMain>
                    <NotificationSystem ref={ this.notificationSystem } />
                </>)
    }
}
