import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError, GetDropDownValues } from "../../utils/generals";

export default class PrescribedThresholdListPage extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();

    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/PrescribedThreshold/GetAddPageData',
            { },
            successfulResponse =>
            {
                this.setState({
                    dropDowns: {
                        personTypes: GetDropDownValues(Constants.DropDowns.PersonTypeId, successfulResponse.data),
                        ageScopeId: GetDropDownValues(Constants.DropDowns.AgeScopeId, successfulResponse.data),
                        amountThresholdId: GetDropDownValues(Constants.DropDowns.AmountThresholdId, successfulResponse.data)
                    }
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <PageMain context={ this } title="لیست آستانه مقرر" icon="list-search" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                    title: "افزودن آستانه مقرر",
                    url: GetUrl("prescribedThresholdAddPage"),
                    color: "outline-primary",
                    icon: "layers-difference",
                    },
                ]}>
                <div className="card">
                    <div className="card-body">
                        {
                            this.state.dropDowns && 
                                <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                                filterBgColor="primary" name="crdthrList" title="جزئیات آستانه مقرر" notificationSystem={ this.notificationSystem }
                                enableAutoFetch listApiUrl="/api/amls//" editPageUrl={ GetUrl("prescribedThresholdEditPage" ) }
                                deleteApiUrl="/api/amls//" onDeleteErrorFunc={ this.onDeleteErrorFunc }
                                headers={[
                                    { title: "نوع شخص", name: "personTypeName" },
                                    { title: "سن", name: "ageScopeName" },
                                    { title: "دوره تراکنش", name: "transactionTypeName" },
                                    { title: "سپرده تجاری", name: "isCommercial", type: "bool" },
                                    { title: "آستانه مقرر", name: "amountThreshold" }
                                ]}
                                filterOptions={[
                                    { type: 'select', name: 'personTypeId', title: 'نوع شخص', selectOptions: this.state.dropDowns.personTypes },
                                    { type: 'select', name: 'ageScopeId', title: 'وضعیت شغل', selectOptions: this.state.dropDowns.ageScopeId },
                                    { type: 'select', name: 'transactionTypeId', title: 'وضعیت مالیات', selectOptions: this.state.dropDowns.amountThresholdId },
                                ]} />
                        }
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                            بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
