import React from "react";
import { TextBox, Helpers, PageMain, SimpleGrid, Card, DropDown } from "core";
import NotificationSystem from 'react-notification-system'
import { GetUrl, HandleError } from '../utils/generals'
import Constants from '../utils/constants'

export default class RequestFormListPage extends React.Component
{
    // Constructor
    constructor(props)
    {
        super(props);
        this.notificationSystem = React.createRef();
        this.state = {
          Forms: null,
          isLoading: true,
          updateKey: 1,
          ButtonsDisabled: false,
          ChecckedFormIds: null,
          NextRequestStates: null
        };
        this.requestId = this.props.match.params.requestId;

        this.SetNextRequestState = this.SetNextRequestState.bind(this);
        this.onSelect = this.OnFormChecked.bind(this);
    }

    // Functions.
    GetFormStates()
    {
      var items = [];

      items.push({ title: Constants.RequestFormState.NotFilled.Title,
                   value: Constants.RequestFormState.NotFilled.Value.toString(),
                   label: Constants.RequestFormState.NotFilled.Title });
      items.push({ title: Constants.RequestFormState.Filled.Title,
                   value: Constants.RequestFormState.Filled.Value.toString(),
                   lable: Constants.RequestFormState.Filled.Title });
      items.push({ title: Constants.RequestFormState.RequestToReFill.Title,
                   value: Constants.RequestFormState.RequestToReFill.Value.toString(),
                   label: Constants.RequestFormState.RequestToReFill.Title });
      items.push({ title: Constants.RequestFormState.Confirmed.Title,
                   value: Constants.RequestFormState.Confirmed.Value.toString(),
                   label: Constants.RequestFormState.Confirmed.Title });
      return items;
    }

    // Events.
    componentDidMount()
    {
      Helpers.CallServer('/api/FRQST/Request/GetRequestStatusInfo',
      { id: this.requestId },
      successfulResponse =>
      {
          var nextRequestStates = [];

          successfulResponse.data.nextRequestStates.forEach(state =>
            nextRequestStates.push({ label: state.value, value: state.key, title: state.value }));
          this.setState({
            NextRequestStates: nextRequestStates,
            ButtonsDisabled: nextRequestStates.length == 0
          });
      },
      errorResponse => {
          return HandleError(Helpers, this.notificationSystem, errorResponse);
      },
      () =>
      {
          this.setState({ isLoading: false });
      })
    }

    SetNextRequestState()
    {
      var nextRequestStateId = this.state["nextRequestStates"];

      if(nextRequestStateId == null || nextRequestStateId == '')
      {
        Helpers.DisplayWarningMessage(this.notificationSystem, null, 'وضعیت درخواست را انتخاب نمایید');
        return;
      }

      this.setState({ isLoading: true });
      Helpers.CallServer('/api/FRQST/Request/SetNextRequestState',
      {
        RequestId: this.requestId,
        Comment: this.state['comment'],
        NextRequestStateId: nextRequestStateId,
        RequestFormIds: this.state.ChecckedFormIds
      },
      successfulResponse => {
        var nextRequestStates = [];

        if(successfulResponse.data.nextRequestStates != null)
          successfulResponse.data.nextRequestStates.forEach(state => nextRequestStates.push({ label: state.value, value: state.key, title: state.value }));
        this.setState({
            comment: null,
            NextRequestStates: nextRequestStates,
            ButtonsDisabled: nextRequestStates.length == 0,
            updateKey: this.state.updateKey + 1
        });
          Helpers.DisplaySuccessMessage(this.notificationSystem, 'وضعیت جدید در خواست با موفقیت ثبت شد');
      },
      errorResponse => {
          return HandleError(Helpers, this.notificationSystem, errorResponse);
      },
      () =>
      {
        this.setState({ isLoading: false });
      })
    }

    OnFormChecked(checkedItems)
    {
      if(checkedItems == null || checkedItems.length <= 0)
          this.setState({ ChecckedFormIds: null });
      else
        this.setState({ ChecckedFormIds: checkedItems });
    }

    render()
    {
        return this.state.NextRequestStates != null && (<>
                <PageMain
                    title="فهرست فرم ها"
                    icon="list-details"
                    context={ this }
                    topButtons={[{ title: "فهرست درخواست", icon: "list-details", color: "outline-success", url: GetUrl("requestList") } ]}
                    isLoading={ false }
                    footerLinks={[
                    { href: "/", text: "صفحه اصلی" },
                    { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" },
                    ]}>                      
                  <Card
                      title=''
                      context={ this }
                      submitUrl=''
                      hasBackButton={ false }
                      hasDefaultSaveButton= { false }
                      cssClassName='CardWithoutHeader'
                      updateKey={ this.state.updateKey }>   

                        <SimpleGrid context={ this }
                              name="grid"
                              title="نمایش فرم ها"
                              enableAutoFetch
                              listApiUrl={ '/api/FRQST/Request/GetRequestForms/' }
                              extraItemsSentToFetchData={ { Id: this.requestId } }
                              idFieldName="formId"
                              checkBoxAndAction={ Helpers.IsInRole(Constants.Roles.BranchCreditChairman) || Helpers.IsInRole(Constants.Roles.RegionManager) || Helpers.IsInRole(Constants.Roles.Center)
                                          ? { title: '', onSelectItemFunc: (chekcedItems) => this.OnFormChecked(chekcedItems) }
                                          : null
                                        }
                              disabledPaging
                              headers={[{ title: "نام فرم", name: "formTitle" },
                                        { title: 'اجباری', name: 'isRequired' },
                                        { title: "وضعیت", name: "requestFormStateName" },
                                        { title: "تاریخ تکمیل", name: "date" }
                                      ]}
                              hideExportButton
                              minHeightTableWrapper={ 200 }
                              badgeOnCells={[
                                  {
                                    fieldName: "requestFormStateName",
                                    color: (x) => {
                                      switch (x) {
                                        case Constants.RequestFormState.NotFilled.Title:
                                          return "red";
                                        case Constants.RequestFormState.Confirmed.Title:
                                          return "green";
                                        case Constants.RequestFormState.RequestToReFill.Title:
                                          return "yellow"
                                        case Constants.RequestFormState.Filled.Title:
                                          return "orange"
                                      }
                                    },
                                  },
                                ]}
                                filterOptions={ [{ type: 'select', name: 'requestFormStateId', title: 'وضعیت', selectOptions: this.GetFormStates() }] }
                                moreInfoLinks={[{ title:'تکمیل فرم', icon:'edit', url: GetUrl('dynamicFormPage/#replaceWith_requestId#/#replaceWith_formId#/#replaceWith_formTitle#') }]}
                                notificationSystem={ this.notificationSystem } />
                              <div className="row" style={{ margin: '10px' }}>
                                <div className="col-12">
                                <TextBox context={ this }
                                    updateKey={ this.state.updateKey }
                                    name="comment"
                                    title="توضیحات"
                                    multiline
                                    lgCol={ 12 }
                                    minLength={ 3 }
                                    maxLength={ 512 } />
                                </div>
                              </div>
                              <div className="row" style={{ margin: '10px' }}>
                                <DropDown context={ this }
                                  name="nextRequestStates"
                                  title="وضعیت درخواست"
                                  required
                                  options={ this.state.NextRequestStates } /> 
                                  <div className="col-md-6 col-lg-4 my-3">                                      
                                        <label style={{ "marginTop": "7%" }}>
                                            <button
                                              type="button"
                                              disabled={ this.state.ButtonsDisabled }
                                              className="btn btn-primary align-bottom"
                                              style={ { width: '140px', height: '40px' } }
                                              onClick={ this.SetNextRequestState }>تایید</button>
                                        </label>
                                  </div>
                              </div>
                          </Card>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
                </>)
    }
}
