import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from 'react-notification-system'
import ClosingRequestSection from '../../sections/closingRequestSection'
import { GetUrl, HandleError } from '../../utils/generals'

export default class ClosingRequest extends React.Component
{
    constructor(props)
    {
        super(props);
        this.requestId = this.props.match.params.requestId;
        this.requestName = this.props.match.params.name;
        this.state = {
            isLoading: true,
            ClosedRequest: null
        };
        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/FRQST/Request/GetClosedRequest',
                            { Id: this.requestId, },
                            successfulResponse =>
                            {
                                this.setState({ ClosedRequest: successfulResponse.data });
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () =>
                            {
                                // Do tasks if it is neccessary
                                console.log('Done');
                                this.setState({ isLoading: false });
                            })
    }

    render()
    {
        return this.state.ClosedRequest != null && <>
                <PageMain
                    title='مختومه کردن درخواست ها'
                    icon="article-off"
                    isLoading={ this.state.isLoading }
                    context={ this }
                    footerLinks={[
                    { href: "/", text: "صفحه اصلی" },
                    { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" },
                    ]}>
                        <ClosingRequestSection requestId={ this.requestId } requestName={ this.requestName } closedRequestData={ this.state.ClosedRequest } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>;
    }
}
