import React from "react";
import { ModalHelper } from "core";

export default class ConfirmBox extends React.Component
{
    constructor(props)
    {
        super(props);

        this.state = { modalIsSubmitting: false };

        this.modalConfirm = this.modalConfirm.bind(this);
    }

    // Events.
    modalConfirm(modal)
    {
        if(modal == undefined)
        {
            if(this.props.onConfirm != null)
                this.props.onConfirm();
        }
        else
        {
            if(this.props.onCancel != null)
                this.props.onCancel();
        }
    }

    render()
    {
        return (<ModalHelper context={ this }
                       open={ this.props.modalIsOpen }
                       title={ this.props.title }
                       buttonTitle='   تایید   '
                       buttonClass='btn-primary width-100'
                       isSubmitting={ this.state.modalIsSubmitting }
                       buttonIcon='check'
                       size='lg'
                       toggle={ this.modalConfirm }
                       onClick={ this.modalConfirm }>

              <div className="rdow">
                <div className="col">
                    { this.props.message }
                </div>
              </div>
          </ModalHelper>);
    }
}
