import React from "react";
import { PageMain, Helpers,Card ,TextBox,DropDown} from "core";
import NotificationSystem from "react-notification-system";
import { jobOptions } from "../../utils/selectOptionsUtils";

class PersonalInformationPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        tabNumber: 0,
        isLoading: false,
        updateKey: 1,
      };
      this.tabNumber = props.tabNumber;
      this.pageInfo = {
       title: "اطلاعات شخصی اصلی موضوع گزارش",
        icon: "circle-plus",
      };
      this.notificationSystem = React.createRef();
    }
    render() {
      return (
        <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
         hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام / نام شرکت"
              notificationSystem={this.notificationSystem}
              required
            />
       <DropDown
            context={this}
            lgCol={3}
            mdCol={12}
            title="شغل شخص"
            name="group"
            notificationSystem={this.notificationSystem}
            options={jobOptions()}
            required
          />
        <TextBox
              context={this}
              lgCol={3}
              mdCol={12}
              name="age"
              title="شغل شخص"
              notificationSystem={this.notificationSystem}
              required
            />
     <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام خانوادگی"
              notificationSystem={this.notificationSystem}
              required
            />
     <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="آدرس محل سکونت"
              notificationSystem={this.notificationSystem}
              required
            />
     <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام پدر"
              notificationSystem={this.notificationSystem}
              required
            />
     <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="کد پستی محل سکونت"
              notificationSystem={this.notificationSystem}
              required
            />
     <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="شماره ملی/ شناسه ملی /شماره فراگیر / شماره اختصاصی اتباع"
              notificationSystem={this.notificationSystem}
              required
            />
     <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="آدرس محل فعالیت"
              notificationSystem={this.notificationSystem}
              required
            />
 <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title=" شماره گذر نامه /شماره شناسنامه / شماره ثبت"
              notificationSystem={this.notificationSystem}
              required
            />
 <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="کد پستی محل فعالیت"
              notificationSystem={this.notificationSystem}
              required
            />
 <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="شغل ونوع فعالیت"
              notificationSystem={this.notificationSystem}
              required
            />
 <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="تلفن تماس"
              notificationSystem={this.notificationSystem}
              required
            />
        </Card>
        
          <NotificationSystem ref={this.notificationSystem} />
        </>
      );
    }
  }
  
  export default PersonalInformationPage ;
