import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import PrescribedThresholdSection from '../../sections/prescribedThresholdSection'
import { GetUrl, HandleError, GetDropDownValues } from "../../utils/generals";
import Constants from "../../utils/constants"

export default class PrescribedThresholdAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            dropDowns: null,
            isLoading: true
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/PrescribedThreshold/GetAddPageData',
            { },
            successfulResponse =>
            {
                this.setState({
                    dropDowns: {
                        personTypes: GetDropDownValues(Constants.DropDowns.PersonTypeId, successfulResponse.data),
                        jobTypes: GetDropDownValues(Constants.DropDowns.AgeScopeId, successfulResponse.data),
                        taxStates: GetDropDownValues(Constants.DropDowns.AmountThresholdId, successfulResponse.data)
                    }
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <PageMain title="افزودن آستانه مقرر" icon="layers-difference" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                topButtons={[
                    {
                        title: "لیست سطح فعالیت مشتری",
                        url: GetUrl("customerActivityLevelListPage"),
                        color: "outline-primary",
                        icon: "list-search",
                    },
                ]}>
                {
                    <PrescribedThresholdSection record={ null } dropDowns={ this.state.dropDowns } />
                }
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
