import React from "react";
import { PageMain, SimpleGrid } from "core";
import NotificationSystem from 'react-notification-system'
import { GetUrl } from '../../utils/generals'

export default class ClosingRequestList extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = { };
        this.notificationSystem = React.createRef();
    }

    // Events.
    render()
    {
        return <>
                <PageMain
                    title='لیست درخواست های مختومه'
                    icon="article-off"
                    context={ this }
                    footerLinks={[
                    { href: "/", text: "صفحه اصلی" },
                    { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" },
                    ]}>
                        <SimpleGrid context={ this }
                            name="grid"
                            title="جزئیات درخواست های مختومه"
                            enableAutoFetch
                            listApiUrl='/api/FRQST/Request/GetClosedRequestList'
                            headers={[
                                        { title: "کد رهگیری", name: "trackingCode" },
                                        { title: "نام شرکت", name: "name" },
                                        { title: "شناسه ملی شرکت", name: "nationalCode" },
                                        { title: "شماره مشتری", name: "customerNumber" },
                                        { title: "شعبه", name: "branchName" },
                                        { title: "وضعیت", name: "requestStatusTitle" }
                                    ]}
                            hideExportButton
                            minHeightTableWrapper={ 200 }
                            moreInfoLinks={[{ title:'نمایش', icon:'eye', url: GetUrl('closingRequestView/#replaceWith_id#/#replaceWith_name#') }]}
                            notificationSystem={ this.notificationSystem } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>;
    }
}
