import { Component } from "react";
import React from "react";
import { Card, DropDown, Helpers, DatePicker, TextBox, Delimiter, AutoComplete } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"

export default class CustomerActivityLevelSection extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            controls: null
        };

        this.notificationSystem = React.createRef();

        this.onPersonTypeSelected = this.onPersonTypeSelected.bind(this);
    }

    GetRealPersonControls()
    {
        var controls  = [];

        controls.push(<DropDown context={ this } name="jobTypeId" title="وضعیت اشتغال" options={ [] } required />);
        controls.push(<AutoComplete context={ this } name="jobId" title="شغل" notificationSystem={ this.notificationSystem } required
                        remoteUrl="/api/amls//"
                        externalData={ this.props.record == null
                            ? []
                            : [{
                                label: this.props.record.regionName,
                                value: this.props.record.regionCode
                            }]} />);

        return controls;
    }

    // Events.
    onPersonTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({ controls: null });
            return;
        }
        var personTypeId = item.value;

    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن سطح فعالیت مشتری" : "جزئیات ویرایش سطح فعالیت مشتری" } name="crdCustomer"
                    statusColor="primary" hasDefaultSaveButton submitUrl={ this.props.record == null ? "/api/amls//" : "/api/amls//" }
                    hasBackButton icon="user-circle" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}
                    onBeforeSubmit={ this.onBeforeSubmit } onSubmitSuccess={ this.onSubmitSuccess } updateKey={ this.state.updateKey }>

                    <DatePicker context={ this } name="expirationDateFrom" title="تاریخ اعتبار از" useEpoch required />
                    <TextBox context={ this } name="allowedActivityLevel" title="سطح فعالیت مجاز" required commaSeparated minLength={ 1 } maxLength={ 15 }
                        validateHandler={[ Helpers.NumberCommaSeparated, Helpers.NumberTypeValidator, Helpers.MinMaxLengthValidator ]} leftText="ریال" />
                    <TextBox context={ this } name="allowedLevelAboveActivityLevelPercent" title="سطح مجاز بالاتر از سطح فعالیت" minLength={ 1 } maxLength={ 3 } required
                        validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]} leftIcon="percentage" />
                    <DropDown context={ this } name="personTypeId" title="نوع شخص" options={ [] } required
                        changeHandler={ this.onPersonTypeSelected }  />
                    <Delimiter></Delimiter>
                    { this.state.controls }
                    

                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
