import { Card, AutoComplete,DatePicker,  Table  ,PageMain, Helpers} from "core";
import React from "react";
import NotificationSystem from "react-notification-system";
import Constants from "../../utils/constants";

export default class DistributionOfIndicatorsPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      updateKey: 1
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>
        <PageMain title="توزیع شاخص" icon="building" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
            topButtons={[]}>
        <Card context={ this } title="جزئیات توزیع شاخص" icon="file-text" hasDefaultSaveButton hasBackButton statusColor="primary">
          
          <AutoComplete context={ this } name="personCode" title="پرسنل" lgCol={4} mdCol={4} required remoteUrl="/api/amls/DistributionOfIndicator/SearchPersonels" />

          <DropDown context={ this } name="indexId" title="نوع شخص" options={ this.props.dropDowns.personTypes } required />

          <DatePicker context={this}
            name="expirationDateFrom"
            title="تاریخ اعتبار از"
            useEpoch
            required />

          <div className="row">
            <div className="col-10"></div>
            <div className="col p-2">
                <a className="btn btn-sm btn-outline-primary float-end" onClick={ this.AddRegionGroup }>
                    <i className="ti me-2 ti-circle-plus" />افزودن
                </a>
            </div>
          </div>
          <Table
            minHeightTableWrapper={ Constants.MinGridHeight }
            //data=[]{this.props.dataList1}
            headers={[
              { title: "نام کد", name: "name" },
              { title: "کد شاخص", name: "group" },
              { title: "تاریخ اعتبار از", name: "name" },
              { title: "نام پرسنل", name: "group" },
            ]}
            hasDeleteButton
            onDeleteButton={(e) => this.props.onDelete(e)}
          />
 
        </Card>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
