import React from "react";
import { PageMain, Helpers, Card, DropDown } from "core";
import NotificationSystem from "react-notification-system";
import { groupOptions } from "../../utils/selectOptionsUtils";

class PajhwokPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title:"پژواک",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >

          <DropDown
            context={this}
            lgCol={6}
            mdCol={12}
            title="شماره گروه بندی اتباع"
            name="group"
            notificationSystem={this.notificationSystem}
            options={groupOptions()}
            required
          />
        </Card>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default PajhwokPage;
