import { Component } from "react";
import React from "react";
import { Helpers, PageMain, Card, TextBox, DropDown, DatePicker } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from '../../../utils/generals'
import Constants from "../../../utils/constants";

export default class RequestBillingAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();

        this.personNumberTypes = [
            Constants.PersonNumberType.Real,
            Constants.PersonNumberType.Legal,
            Constants.PersonNumberType.Foreign
        ];
        this.personNumber = null;

        this.onPersonNumberTypeSelected = this.onPersonNumberTypeSelected.bind(this);
    }

    // Events.
    onPersonNumberTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.personNumber = null;
            return;
        }
        switch(item.value)
        {
            case Constants.PersonNumberType.Real.value:
                this.personNumber = "کد ملی";
            break;
            case Constants.PersonNumberType.Legal.value:
                this.personNumber = "شناسه ملی";
            break;
            case Constants.PersonNumberType.Foreign.value:
                this.personNumber = "کد فراگیر اتباع";
            break;
            default:
                this.personNumber = null;
            break;
        }
    }

    render()
    {
        return (
            <>
                <PageMain title="افزودن درخواست صورتحساب" icon="receipt" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                    topButtons={[
                        {
                            title: "لیست درخواست صورتحساب",
                            url: GetUrl("requestBillingListPage"),
                            color: "outline-primary",
                            icon: "list-search",
                        },
                    ]}>
                        <Card context={ this } title="جزئیات افزودن درخواست صورتحساب" name="crdRequestBilling" statusColor="primary" hasDefaultSaveButton
                            submitUrl="/api/amls/RegionInfo/AddRegionInfo" hasBackButton icon="receipt"
                            onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}>



                                <TextBox context={ this } name="customerNumber" title="شماره مشتری" minLength={ 1 } maxLength={ 25 } />

                                <DropDown context={ this } name="personNumberTypeId" title="نوع شخص" options={ this.personNumberTypes } changeHandler={ this.onPersonNumberTypeSelected } />
                                <TextBox context={ this } name="number" title={ this.personNumber == null ? "شماره" : this.personNumber } minLength={ 1 } maxLength={ 25 } readOnly={ this.personNumber == null }/>

                                <TextBox context={ this } name="accountNumber" title="شماره حساب" minLength={ 1 } maxLength={ 25 } />
                                <TextBox context={ this } name="cardNumber" title="شماره کارت" minLength={ 1 } maxLength={ 25 } />

                                <DatePicker context={ this } name="requestDate" title="تاریخ درخواست" useEpoch />

                                <DatePicker context={ this } name="transactionFromDate" title="از تاریخ تراکنش" useEpoch />
                                <DatePicker context={ this } name="transactionToDate" title="از تاریخ تراکنش" useEpoch />

                        </Card>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
