import { Component } from "react";
import React from "react";
import { PageMain, Card, TextBox } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from '../../../utils/generals'

export default class RequestBillingAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    render()
    {
        return (
            <>
                <PageMain title="افزودن مناطق" icon="building" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                    topButtons={[
                        {
                            title: "افزودن درخواست صورتحساب",
                            url: GetUrl("requestBillingListPage"),
                            color: "outline-primary",
                            icon: "receipt",
                        },
                    ]}>
                        <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن مناطق" : "جزئیات ویرایش مناطق" } name="crdRegionInfo" statusColor="primary" hasDefaultSaveButton
                            submitUrl={ this.props.record == null ? "/api/amls/RegionInfo/AddRegionInfo" : "/api/amls/RegionInfo/UpdateRegionInfo" } hasBackButton icon="building"
                            onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}>

                                <TextBox context={ this } name="customerNumber" title="شماره مشتری" minLength={ 1 } maxLength={ 64 } required validateHandler={[ Helpers.MinMaxLengthValidator ]}  />

                        </Card>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
