import React from 'react';
import { Card, TextBox, Table, Delimiter, Helpers } from "core";
import NotificationSystem from 'react-notification-system';
import TableItem from '../../models/tableItem'
import { GetUrl, HandleError } from '../../utils/generals'
import DropDownItem from '../../models/dropDownItem'
 
const TableColumns = {
  Name: 'name',
  ParameterLable: 'label'
};

const ControlsName = [ TableColumns.ParameterLable ];

const TableRows = 'TableRows';
const Values = 'Values';

class DropDownSection extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          TableRows: [],
          updateKey: 1,
          Values: [],
          Id: (this.props.dropDown == null ? null : this.props.dropDown.id),
          ShowSaveButton: true
        };

        this.notificationSystem = React.createRef();
        this.rowId = 1;

        this.AddParamneter = this.AddParamneter.bind(this);
        this.IsParameterValid = this.IsParameterValid.bind(this);
        this.IsParametersValid = this.IsParametersValid.bind(this);
        this.DeleteParameter = this.DeleteParameter.bind(this);
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.onSubmitError = this.onSubmitError.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);

        if(this.props.dropDown != null)
          this.DropDownName = this.props.dropDown.name;
    }

    // Functions.
    IsParameterValid(name)
    {
      var msg = "";

      if(this.state[name + 'IsValid'] == false)
      {
        msg = this.state[name + 'ValidFeedBack'];
        Helpers.DisplayErrorMessage(this.notificationSystem, null, msg);
        return false;
      }

      return true;
    }

    IsParametersValid()
    {
      var isValid = true;

      ControlsName.forEach(ctl =>
        {
            if(isValid == false)
              return;
            isValid = this.IsParameterValid(ctl);
        });
      return isValid;
    }

    // Events.
    componentDidMount()
    {
      if(this.props.dropDown == null)
        return;
      var tableRows = this.state.TableRows;
      var records = this.state.Values;
      var row = [];

      this.props.dropDown.values.forEach(rw =>
      {
          row.push(new TableItem(TableColumns.ParameterLable, rw.label, {
            RowId: this.rowId,
            Id: this.props.dropDown.id
          }));
          records.push(new DropDownItem(rw.title, rw.value, rw.label, this.rowId));
          tableRows.push(row);
          row = [];
          this.rowId++;
      });
    }

    AddParamneter()
    {
        var records = this.state.Values;

        if(this.IsParametersValid() == false)
          return;
        var tableRows = this.state.TableRows;
        var row = [];
        var state = {};
        var label = null;
        var temp = this.state[TableColumns.ParameterLable];
        
        row.push(new TableItem(TableColumns.ParameterLable, temp, {
          RowId: this.rowId,
          Id: null
        }));
        label = temp;
        state[TableColumns.ParameterLable] = null;    
        
        records.push(new DropDownItem(label, null, label, this.rowId));
        this.rowId++;
        tableRows.push(row);
        state[TableRows] = tableRows;
        state['updateKey'] = this.state.updateKey + 1;
        state[Values] = records;
        this.setState(state);
    }

    DeleteParameter(option)
    {
        var tableRows = this.state.TableRows;
        var records = this.state.Values;
        var row = option.modalData;
        var extraData = row[0].ExtraData;

        tableRows = tableRows.filter(rw => rw[0].ExtraData.RowId != extraData.RowId);
        records = records.filter(rd => rd.ExtraData != extraData.RowId);
        
        this.setState({
          TableRows: tableRows,
          Values: records,
          updateKey: this.state.updateKey + 1
        });
    }

  onBeforeSubmit() {
      var result = this.IsParameterValid(TableColumns.Name);

    if(result)
      this.DropDownName = this.state[TableColumns.Name];
    return result;
  }

  onSubmitError(er) {
      return HandleError(Helpers, this.notificationSystem, er);
  }

  onSubmitSuccess()
  {
      this.setState({
        ShowSaveButton: this.props.dropDown != null,
        updateKey: this.state.updateKey + 1,
        [TableColumns.Name]: this.DropDownName
      });
      Helpers.DisplaySuccessMessage(this.notificationSystem, 'اطلاعات با موفقیت ذخیره شد');
      return true;
  }

    render() {
        return (
          <>
            <Card
              context={ this }
              title="جزئیات داده های لیستی"
              icon="layout-list" 
              hasBackButton={ true }
              hasDefaultSaveButton={ this.state.ShowSaveButton }
              statusColor="success"
              updateKey= { this.state.updateKey }
              submitUrl='/api/FRQST/DropDown/SaveParameters'
              onSubmitError={ this.onSubmitError }
              onBeforeSubmit={ this.onBeforeSubmit }
              onSubmitSuccess={ this.onSubmitSuccess }
              ignoreValidation={ true }
              topButtons={[
                {
                  title: "لیست داده های شمارشی",
                  icon: "list-details",
                  color: "outline-success",
                  url: GetUrl("dropDownListPage"),
                },
              ]}>

              <TextBox
                      context={ this }
                      name={ TableColumns.Name }
                      title="نام داده شمارشی"
                      required
                      minLength={ 1 }
                      maxLength={ 64 }
                      validateHandler={ Helpers.MinMaxLengthValidator }
                      initialValue={ this.DropDownName == null ? null : this.DropDownName } /> 

              <Delimiter />

              <TextBox
                      context={ this }
                      name={ TableColumns.ParameterLable }
                      title="برچسب پارامتر"
                      required
                      minLength={ 1 }
                      maxLength={ 32 }
                      validateHandler={ Helpers.MinMaxLengthValidator } /> 

              <div className="row">
                  <div className="col p-2">
                      <a className="btn btn-sm btn-outline-primary float-end" onClick={ this.AddParamneter }>
                          <i className="ti me-2 ti-circle-plus" />افزودن
                      </a>
                  </div>
              </div>

              <div className="row">
                  <div className="col align-self-center">
                      <Table hasDeleteButton
                          headers={[
                            { title: 'برچسب پارامتر', name: TableColumns.ParameterLable },
                          ]}
                          data={ this.state.TableRows }
                          lgCol={ 12 }
                          minHeightTableWrapper={ 200 }
                          onDeleteButton={ this.DeleteParameter } />
                  </div>
              </div>
            </Card>
            <NotificationSystem ref={ this.notificationSystem } />
          </>
        );
    }
}


export default DropDownSection
