import React, { Component } from 'react';
import { PageMain, Helpers } from "core";
import NotificationSystem from 'react-notification-system';
import FormSection from '../../sections/basePage/formSection';
import { HandleError } from '../../utils/generals'

class FormEditPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            FormData: null,
            isLoading: true
         };
        this.notificationSystem = React.createRef();

        this.formId = this.props.match.params.formId;
    }

    componentDidMount()
    {
      Helpers.CallServer('/api/FRQST/Form/GetForm/',
      { Id: this.formId },
      successfulResponse =>
      {
        this.setState({ FormData: successfulResponse.data });
      },
      errorResponse => {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
      },
      () =>
      {
          this.setState({ isLoading: false });
      })
    }

    render() {
        return this.state.FormData != null && (
            <>
                <PageMain title=" فرم" icon="users"
                    context={ this }
                    isLoading={ this.state.isLoading }
                    footerLinks={[{ href: "/", text: "صفحه اصلی" },{ href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" }]}>
                    <FormSection formData={ this.state.FormData } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}

export default FormEditPage
