import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError, GetDropDownValues, DownloadBlobFile } from "../../utils/generals";
import Constants from '../../utils/constants'
import { Backward } from "../../utils/generals";

export default class PersonInfListPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            dropDowns: null
        };

        this.notificationSystem = React.createRef();

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/amls/PersonInfo/GetAddPageData',
          { },
          successfulResponse =>
          {
              this.setState({
                  dropDowns: {
                      states: GetDropDownValues(Constants.DropDowns.PersonStateId, successfulResponse.data),
                      personTypes: GetDropDownValues(Constants.DropDowns.PersonTypeId, successfulResponse.data),
                      personNationalities: GetDropDownValues(Constants.DropDowns.PersonNationalityId, successfulResponse.data)
                  }
              })
          },
          errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
          () => { this.setState({ isLoading: false }) }, 'GET');
  }

  onDeleteErrorFunc(errorResponse)
  {
      return HandleError(Helpers, this.notificationSystem, errorResponse);
  }

  downloadPersonInfoReport()
  {
    this.setState({ isLoading: true });
    Helpers.CallServer(`/api/brs/PersonInfo/PersonInfoReport`,
    {},
    successfulResponse => DownloadBlobFile(successfulResponse, "Branches Included Report.xlsx"),
    errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
    () => this.setState({
      isLoading: false,
    }), 'GET', 'blob');
  }

  render()
  {
    return (
        <>
            <PageMain context={ this } title="لیست اشخاص " icon="users" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
            topButtons={[
                {
                title: "افزودن اطلاعات شخص",
                url: GetUrl("personInfAddPage"),
                color: "outline-primary",
                icon: "user-plus",
                },
            ]}>
            <div className="card">
                <div className="card-body">
                    {
                        this.state.dropDowns && 
                        <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } filterBgColor="primary" name="slpPersons"
                        title="لیست اشخاص" notificationSystem={ this.notificationSystem } enableAutoFetch
                            listApiUrl="/api/amls/PersonInfo/SearchPersons" editPageUrl={ GetUrl("personInfEditPage" ) }
                            deleteApiUrl="/api/amls/PersonInfo/DeletePersonInfo" onDeleteErrorFunc={ this.onDeleteErrorFunc }
                            headers={[
                                { title: "نام", name: "name" },
                                { title: "کد", name: "code" },
                                { title: "وضعیت", name: "statusName" },
                                { title: "نوع شخص", name: "personTypeName" },
                                { title: "تابعیت", name: "personNationalityName" },
                                { title: "فعال", name: "isActive", type: 'bool' }
                            ]}
                            filterOptions={[
                                { type: 'text', name: 'code', title: 'کد' },
                                { type: 'text', name: 'name', title: 'نام' },
                                { type: 'select', name: 'statusId', title: 'وضعیت', selectOptions: this.state.dropDowns.states },
                                { type: 'select', name: 'personTypeId', title: 'نوع شخص', selectOptions: this.state.dropDowns.personTypes },
                                { type: 'select', name: 'personNationalityId', title: 'تابعیت', selectOptions: this.state.dropDowns.personNationalities }
                            ]} />
                    }
                </div>
                <div className="card-footer">
                    <div className="d-flex">
                        <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => Backward(e) } href="/" title="بازگشت به صفحه قبل">
                        بازگشت
                        <i className="ti ti-arrow-left ms-1"></i>
                        </a>
                    </div>
                    </div>
                </div>
            </PageMain>
            <NotificationSystem ref={ this.notificationSystem } />
        </>
    );
  }
}
