import React, { Component } from "react";
import CausalAndRelativeRelationshipSection from "./causalAndRelativeRelationshipSection";
import { Card, DropDown, Helpers, SimpleGrid, TextBox } from "core";
import CtrAndStrSection from "./ctrAndStrSection";
import TabSection from "../../componet/tab";
import constants from "../../utils/constants";
import Button from "../../componet/button";
import RealCustomerInformationSection from "../../sections/customerProfile/realCustomerInformationSection";
import {
  IsLegalPersonNationalIdValid,
  IsRealPersonNationalIdValid,
} from "../../utils/nationalIdentification";
import NotificationSystem from "react-notification-system";

export default class CustomerProfileSection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      updateKey: 1,
      stepNumber: 0,
      showTab: false,
      headers: [],
      showSearchItem: true,
    };
    this.PersonTypes = [
      constants.PersonNumberType.Real,
      constants.PersonNumberType.Legal,
      constants.PersonNumberType.Foreign,
    ];
    this.personNumber = {
      Title: null,
      MaxLength: null,
    };
    this.notificationSystem = React.createRef();
    this.onRemoveFilter = this.onRemoveFilter.bind(this);
  }

  getCardTitle = () => {
    let title = "";
    if (this.state.stepNumber == 0) {
      title = "اطلاعات هویتی";
    } else if (this.state.stepNumber == 1) {
      title = "روابط نسبی و سببی";
    } else if (this.state.stepNumber == 2) {
      title = "CTR/STR";
    }
    return title;
  };

  onChangeStepNumber = (e) => {
    if (this.state.stepNumber < e) {
      this.setState({
        stepNumber: e,
      });
    } else {
      this.setState({
        stepNumber: e,
      });
    }
  };

  onPersonNumberTypeSelected = (item) => {
    if (item == null || item.value == null || item.value == "") {
      this.personNumber = {
        Title: null,
        MaxLength: null,
      };
      return;
    }
    switch (item.value) {
      case constants.PersonNumberType.Real.value:
        this.personNumber = {
          Title: "کد ملی",
          MaxLength: 10,
        };
        break;
      case constants.PersonNumberType.Legal.value:
        this.personNumber = {
          Title: "شناسه ملی",
          MaxLength: 11,
        };
        break;
      case constants.PersonNumberType.Foreign.value:
        this.personNumber = {
          Title: "کد فراگیر اتباع",
          MaxLength: 12,
        };
        break;
      default:
        this.personNumber = null;
        break;
    }
    if (item.value == constants.PersonNumberType.Legal.value) {
      this.state.headers = [
        { title: "نوع شخص", name: "personTypeName" },
        {
          title: "نام شرکت",
          name: "companyName",
        },
        {
          title: "از تاریخ",
          name: "fromDate",
        },
        { title: "تا ناریخ", name: "toDate" },
      ];
    } else {
      this.state.headers = [
        { title: "نوع شخص", name: "personTypeName" },
        { title: "نام", name: "firstName" },
        {
          title: "نام خانوادگی",
          name: "lastName",
        },
        { title: "نسبت", name: "relationType" },
      ];
    }
  };

  onSearch = () => {
    var personNumberTypeId = this.state.personNumberTypeId;
    if (personNumberTypeId == null || personNumberTypeId == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "نوع شخص را وارد نمایید"
      );
      return false;
    }

    var number = this.state.number;
    if (number == null || number == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "کد ملی یا شناسه ملی یا کد اتباع را وارد نمایید"
      );
      return false;
    }

    if (number != null || number != "")
      switch (this.state.personNumberTypeId) {
        case constants.PersonNumberType.Real.value:
          if (IsRealPersonNationalIdValid(number) == false) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "کد ملی صحیح نمی باشد"
            );
            return false;
          }
          break;
        case constants.PersonNumberType.Legal.value:
          if (IsLegalPersonNationalIdValid(number) == false) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "شناسه ملی صحیح نمی باشد"
            );
            return false;
          }
          break;
      }
    this.setState({
      showTab: true,
    });
    // window.open("/UI/customerProfileListPage");
    return true;
  };

  onRemoveFilter = () => {
    this.setState({
      personNumberTypeId: null,
      number: null,
      accountNumber: null,
      customerNumber: null,
      showTab: false,
      updateKey: this.state.updateKey + 1,
    });
    this.personNumber = {
      Title: null,
    };
  };

  render() {
    return (
      <>
        <div className="card">
          <div className="card-body">
            {this.state.showSearchItem && (
              <div className="row" key={this.state.updateKey}>
                <DropDown
                  context={this}
                  mdCol={4}
                  lgCol={4}
                  name="personNumberTypeId"
                  title="نوع شخص"
                  options={this.PersonTypes}
                  required
                  changeHandler={(e) => this.onPersonNumberTypeSelected(e)}
                />
                <TextBox
                  context={this}
                  name="number"
                  title={
                    this.personNumber.Title == null
                      ? "شماره"
                      : this.personNumber.Title
                  }
                  minLength={1}
                  maxLength={
                    this.personNumber.MaxLength == null
                      ? 1
                      : this.personNumber.MaxLength
                  }
                  validateHandler={[Helpers.MinMaxLengthValidator]}
                  mdCol={4}
                  lgCol={4}
                  readOnly={this.personNumber.Title == null}
                />
                <div mdCol={4} lgCol={4}></div>
                <TextBox
                  context={this}
                  name="customerNumber"
                  title="شماره مشتری"
                  minLength={1}
                  maxLength={25}
                  validateHandler={[Helpers.MinMaxLengthValidator]}
                  mdCol={4}
                  lgCol={4}
                />
                <TextBox
                  context={this}
                  name="accountNumber"
                  title="شماره حساب"
                  minLength={1}
                  maxLength={25}
                  validateHandler={[Helpers.MinMaxLengthValidator]}
                  mdCol={4}
                  lgCol={4}
                />
                <div className="text-start col-lg-4 col mt-3 me-4">
                  <Button
                    buttonClass={`btn btn-outline-primary col-lg-1.5 mx-1`}
                    text="درخواست جستجو"
                    onclick={() => this.onSearch()}
                  />
                  <Button
                    buttonClass={`btn btn-outline-danger col-lg-1.5 mx-4`}
                    text="حذف فیلتر"
                    onclick={() => this.onRemoveFilter()}
                  />
                </div>
              </div>
            )}
            {this.state.showTab && (
              <>
                <TabSection
                  stepNumber={this.state.stepNumber}
                  onChangeStepNumber={this.onChangeStepNumber}
                  data={[
                    { index: 0, title: "اطلاعات هویتی" },
                    { index: 1, title: "روابط نسبی و سببی" },
                    { index: 2, title: "CTR/STR" },
                  ]}
                />
                <div className="tab-content" id="pills-tabContent">
                  {/*تب اطلاعات هویتی*/}
                  <div
                    className={`tab-pane fade ${
                      this.state.stepNumber === 0 ? "show active" : ""
                    }`}
                    id="pills-home"
                    role="tabpanel"
                    aria-labelledby="pills-home-tab"
                  ></div>

                  {/* تب روابط نسبی و سببی */}
                  <div
                    className={`tab-pane fade ${
                      this.state.stepNumber === 1 ? "show active" : ""
                    }`}
                    id="pills-home"
                    role="tabpanel"
                    aria-labelledby="pills-home-tab"
                  >
                    <CausalAndRelativeRelationshipSection
                      parentContext={this}
                      parentState={this.state}
                      onChangeStepNumber={this.onChangeStepNumber}
                      personNumberTypeId={this.state.personNumberTypeId}
                      number={this.state.number}
                      headers={this.state.headers}
                    />
                  </div>

                  {/*تب CTR/STR */}
                  <div
                    className={`tab-pane fade ${
                      this.state.stepNumber === 2 ? "show active" : ""
                    }`}
                    id="pills-home"
                    role="tabpanel"
                    aria-labelledby="pills-home-tab"
                  >
                    <CtrAndStrSection
                      parentContext={this}
                      parentState={this.state}
                      onChangeStepNumber={this.onChangeStepNumber}
                      personNumberTypeId={this.state.personNumberTypeId}
                      number={this.state.number}
                    />
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
