import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import CustomerActivityLevelSection from '../../sections/customerActivityLevelSection'
import { GetUrl, HandleError } from "../../utils/generals";

export default class CustomerActivityLevelAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            perosnTypes: null
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
