                })
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <PageMain title="افزودن سطح فعالیت مشتری" icon="user-circle" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                topButtons={[
                    {
                        title: "لیست سطح فعالیت مشتری",
                        url: GetUrl("customerActivityLevelListPage"),
                        color: "outline-primary",
                        icon: "list-search",
                    },
                ]}>
                {
                    this.state.perosnTypes && <CustomerActivityLevelSection record={ null } perosnTypes={ this.state.perosnTypes } />
                }
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
