import { Component } from "react";
import React from "react";
import { Card, DropDown, Helpers, DatePicker, TextBox } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"

export default class PersonnelInfSecion extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {};

        this.notificationSystem = React.createRef();
    }

    // Events.
    sonPersonTypeSelected(item)
    {

    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن شخص" : "جزئیات ویرایش شخص" } name="crdPersonnelInfo" statusColor="primary"
                    hasDefaultSaveButton submitUrl={ this.props.record == null ? "/api/amls//" : "/api/amls//" }
                    hasBackButton icon="building" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} onSubmitSuccess={ this.onSubmitSuccess }
                    updateKey={ this.state.updateKey }>

                    <DropDown context={ this } name="statusId" title="وضعیت شخص" options={ this.props.dropDowns.states } required />
                    <DropDown context={ this } name="personnelTypeId" title="نوع شخص" options={ this.props.dropDowns.personTypes } required changeHandler={ this.sonPersonTypeSelected }/>
                    <DropDown context={ this } name="personnelNationalityId" title="تابعیت" options={ this.props.dropDowns.personNationalities } required />
                    <DatePicker context={ this } name="expirationDateFrom" title="تاریخ اعتبار از" useEpoch required />
                    <TextBox context={ this } name="name" title="نام" minLength={ 1 } maxLength={ 64 } required validateHandler={[ Helpers.MinMaxLengthValidator ]}  />
                    <TextBox context={ this } name="code" title="کد" minLength={ 11 } maxLength={ 12 } required validateHandler={[ Helpers.MinMaxLengthValidator ]}  />
                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
