import React, { Component } from "react";
import { Delimiter, DropDown, Helpers, PageMain, TextBox } from "core";
import TabSection from "../../componet/tab";
import constants from "../../utils/constants";
import Button from "../../componet/button";
import {
  IsLegalPersonNationalIdValid,
  IsRealPersonNationalIdValid,
} from "../../utils/nationalIdentification";
import RealCustomerInformationSection from "../../sections/customerProfile/realCustomerInformationSection";
import CtrAndStrSection from "../../sections/customerProfile/ctrAndStrSection";
import CausalAndRelativeRelationshipSection from "../../sections/customerProfile/causalAndRelativeRelationshipSection";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";

export default class CustomerProfilePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      updateKey: 1,
      stepNumber: 0,

      isLoading: false,
      showSearchItem: true,
      item: {},
    };
    this.PersonTypes = [
      constants.PersonNumberType.Real,
      constants.PersonNumberType.Legal,
      constants.PersonNumberType.Foreign,
    ];
    this.personNumber = {
      Title: null,
      MaxLength: null,
    };
    this.pageInfo = {
      title: "پروفایل مشتری",
      icon: "user-search",
    };
    this.requestId = this.props.match.params.requestId;
    this.notificationSystem = React.createRef();
    this.onRemoveFilter = this.onRemoveFilter.bind(this);
  }

  getCardTitle = () => {
    let title = "";
    if (this.state.stepNumber == 0) {
      title = "اطلاعات هویتی";
    } else if (this.state.stepNumber == 1) {
      title = "روابط نسبی و سببی";
    } else if (this.state.stepNumber == 2) {
      title = "CTR/STR";
    }
    return title;
  };

  onChangeStepNumber = (e) => {
    if (this.state.stepNumber < e) {
      this.setState({
        stepNumber: e,
      });
    } else {
      this.setState({
        stepNumber: e,
      });
    }
  };

  onPersonNumberTypeSelected = (item) => {
    if (item == null || item.value == null || item.value == "") {
      this.personNumber = {
        Title: null,
        MaxLength: null,
      };
      return;
    }
    switch (item.value) {
      case constants.PersonNumberType.Real.value:
        this.personNumber = {
          Title: "کد ملی",
          MaxLength: 10,
        };
        break;
      case constants.PersonNumberType.Legal.value:
        this.personNumber = {
          Title: "شناسه ملی",
          MaxLength: 11,
        };
        break;
      case constants.PersonNumberType.Foreign.value:
        this.personNumber = {
          Title: "کد فراگیر اتباع",
          MaxLength: 12,
        };
        break;
      default:
        this.personNumber = null;
        break;
    }
  };

  onSearch = () => {
    var personNumberTypeId = this.state.personNumberTypeId;
    if (personNumberTypeId == null || personNumberTypeId == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "نوع شخص را وارد نمایید"
      );
      return false;
    }

    var number = this.state.number;
    if (number == null || number == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "کد ملی یا شناسه ملی یا کد اتباع را وارد نمایید"
      );
      return false;
    }

    if (number != null || number != "")
      switch (this.state.personNumberTypeId) {
        case constants.PersonNumberType.Real.value:
          if (IsRealPersonNationalIdValid(number) == false) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "کد ملی صحیح نمی باشد"
            );
            return false;
          }
          if (number.length < 10) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "کد ملی کمتر از 10 کاراکتر می باشد"
            );
            return false;
          }
          break;
        case constants.PersonNumberType.Legal.value:
          if (IsLegalPersonNationalIdValid(number) == false) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "شناسه ملی صحیح نمی باشد"
            );
            return false;
          }
          if (number.length < 11) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "شناسه ملی کمتر از 11 کاراکتر می باشد"
            );
            return false;
          }
        case constants.PersonNumberType.Foreign.value:
          if (number.length < 12) {
            Helpers.DisplayErrorMessage(
              this.notificationSystem,
              null,
              "کد فراگیر اتباع کمتر از 12 کاراکتر می باشد"
            );
            return false;
          }
          break;
      }

    Helpers.CallServer(
      "/api/amls/Request/RequestSearchCustomerProfile",
      {
        personNumberTypeId: this.state.personNumberTypeId,
        number: this.state.number,
        accountNumber: this.state.accountNumber,
        customerNumber: this.state.customerNumber,
      },
      (successfulResponse) => {
        this.setState({
          item: successfulResponse,
        });
        Helpers.DisplaySuccessMessage(
          this.notificationSystem,
          "درخواست با موفقیت انجام شد ...."
        );
        setInterval(() => {
          window.location.href = GetUrl("customerProfileRequestList", item);
        }, 3000);
      },

      (errorResponse) =>
        HandleError(Helpers, this.notificationSystem, errorResponse),
      () => {
        this.setState({ isLoading: false });
      },
      "POST"
    );

    return true;
  };

  onRemoveFilter = () => {
    this.setState({
      personNumberTypeId: null,
      number: null,
      accountNumber: null,
      customerNumber: null,
      updateKey: this.state.updateKey + 1,
    });
    this.personNumber = {
      Title: null,
    };
  };

  render() {
    return (
      <>
        <PageMain
          title={this.pageInfo.title}
          icon={this.pageInfo.icon}
          isLoading={this.state.isLoading}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
        >
          <div className="card" key={this.state.updateKey}>
            <div className="card-body">
              <div className="row">
                <DropDown
                  context={this}
                  mdCol={4}
                  lgCol={4}
                  name="personNumberTypeId"
                  title="نوع شخص"
                  options={this.PersonTypes}
                  required
                  changeHandler={(e) => this.onPersonNumberTypeSelected(e)}
                  disabled={
                    !this.requestId == null ||
                    !this.requestId == "" ||
                    !this.requestId == undefined
                  }
                />
                <TextBox
                  context={this}
                  name="number"
                  title={
                    this.personNumber.Title == null
                      ? "شماره"
                      : this.personNumber.Title
                  }
                  minLength={1}
                  maxLength={
                    this.personNumber.MaxLength == null
                      ? 1
                      : this.personNumber.MaxLength
                  }
                  validateHandler={[Helpers.MinMaxLengthValidator]}
                  mdCol={4}
                  lgCol={4}
                  readOnly={
                    this.personNumber.Title == null &&
                    (!this.requestId == null ||
                      !this.requestId == "" ||
                      !this.requestId == undefined)
                  }
                />
                <div mdCol={4} lgCol={4}></div>
                <TextBox
                  context={this}
                  name="customerNumber"
                  title="شماره مشتری"
                  minLength={1}
                  maxLength={25}
                  validateHandler={[Helpers.MinMaxLengthValidator]}
                  mdCol={4}
                  lgCol={4}
                  readOnly={
                    !this.requestId == null ||
                    !this.requestId == "" ||
                    !this.requestId == undefined
                  }
                />
                <TextBox
                  context={this}
                  name="accountNumber"
                  title="شماره حساب"
                  minLength={1}
                  maxLength={25}
                  validateHandler={[Helpers.MinMaxLengthValidator]}
                  mdCol={4}
                  lgCol={4}
                  readOnly={
                    !this.requestId == null ||
                    !this.requestId == "" ||
                    !this.requestId == undefined
                  }
                />
                {this.requestId == null ||
                this.requestId == "" ||
                this.requestId == undefined ? (
                  <div className="text-start col-lg-4 col mt-3 me-4">
                    <Button
                      buttonClass={`btn btn-outline-primary col-lg-1.5 mx-1`}
                      text="درخواست جستجو"
                      onclick={() => this.onSearch()}
                    />
                    <Button
                      buttonClass={`btn btn-outline-danger col-lg-1.5 mx-4`}
                      text="حذف فیلتر"
                      onclick={() => this.onRemoveFilter()}
                    />
                  </div>
                ) : null}
              </div>
              {!this.requestId == null ||
              !this.requestId == "" ||
              !this.requestId == undefined ? (
                <>
                  <Delimiter className="mt-2" />
                  <TabSection
                    stepNumber={this.state.stepNumber}
                    onChangeStepNumber={this.onChangeStepNumber}
                    data={[
                      { index: 0, title: "اطلاعات هویتی" },
                      { index: 1, title: "روابط نسبی و سببی" },
                      { index: 2, title: "CTR/STR" },
                    ]}
                  />
                  <div className="tab-content" id="pills-tabContent">
                    {/*تب اطلاعات هویتی*/}
                    <div
                      className={`tab-pane fade ${
                        this.state.stepNumber === 0 ? "show active" : ""
                      }`}
                      id="pills-home"
                      role="tabpanel"
                      aria-labelledby="pills-home-tab"
                    >
                      <RealCustomerInformationSection
                        parentContext={this}
                        parentState={this.state}
                        onChangeStepNumber={this.onChangeStepNumber}
                        personNumberTypeId={this.state.personNumberTypeId}
                        number={this.state.number}
                      />
                    </div>

                    {/* تب روابط نسبی و سببی */}
                    <div
                      className={`tab-pane fade ${
                        this.state.stepNumber === 1 ? "show active" : ""
                      }`}
                      id="pills-home"
                      role="tabpanel"
                      aria-labelledby="pills-home-tab"
                    >
                      <CausalAndRelativeRelationshipSection
                        parentContext={this}
                        parentState={this.state}
                        onChangeStepNumber={this.onChangeStepNumber}
                        requestId={this.requestId}
                        number={this.state.number}
                        personNumberTypeId={this.state.personNumberTypeId}
                      />
                    </div>

                    {/*تب CTR/STR */}
                    <div
                      className={`tab-pane fade ${
                        this.state.stepNumber === 2 ? "show active" : ""
                      }`}
                      id="pills-home"
                      role="tabpanel"
                      aria-labelledby="pills-home-tab"
                    >
                      <CtrAndStrSection
                        parentContext={this}
                        parentState={this.state}
                        onChangeStepNumber={this.onChangeStepNumber}
                        personNumberTypeId={this.state.personNumberTypeId}
                        number={this.state.number}
                        requestId={this.requestId}
                      />
                    </div>
                  </div>
                </>
              ) : null}
            </div>
          </div>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
