import { Component } from "react";
import { Helpers, SimpleGrid } from "core";
import NotificationSystem from "react-notification-system";
import Constants from '../utils/constants';
import { GetUrl, HandleError } from '../utils/generals'

export default class RequestListSection extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {};

        this.notificationSystem = React.createRef();

        this.showMessage = this.showMessage.bind(this);
        this.closeRequest = this.closeRequest.bind(this);
    }   

    // Events.
    downloadBillings(record)
    {
        if(record == null || record.id == null || record.id == "")
        {
          Helpers.DisplayErrorMessage(this.notificationSystem, null, 'محاسبات مربوط به شعبه پیدا نشد');
          return;
        }
        var wm = window.open(GetUrl(`downlaodPersonBillings/${record.id}`), "_target");

        wm.focus();
        wm.onblur = function() { wm.close(); };
    }

    showHideMessage(item)
    {
        return item.errorMessage != null && item.errorMessage != "";
    }

    showMessage(item)
    {
        Helpers.DisplayErrorMessage(this.notificationSystem, null, item.errorMessage);
    }

    showRemoveRequest(item)
    {
        return item.statusId != Constants.RequestState.InProcess
    }

    closeRequest(item)
    {
        if(item == null || item.id == null || item.id == "")
        {
            Helpers.DisplayErrorMessage(this.notificationSystem, null, "شناسه درخواست جهت مختومه کردن پیدا نشد");
            return;
        }
        this.setState({ isLoading: true })
        Helpers.CallServer('/api/amls/Request/CloseRequest/' + item.id,
        { },
        successfulResponse =>
        {
            Helpers.DisplaySuccessMessage(this.notificationSystem, successfulResponse.message);
        },
        errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
        () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <div className="card">
                    <div className="card-body">
                        <SimpleGrid context={ this } minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                            filterBgColor="primary" name="crdrqstList" title="جزئیات فهرست درخواست صورتحساب ها" notificationSystem={ this.notificationSystem } enableAutoFetch
                            listApiUrl="/api/amls/Request/SearchRequests" 
                            headers={[
                                        { title: "کد پرسنل", name: "personCode" },
                                        { title: "نام پرسنل", name: "personName" },
                                        { title: "تاریخ درخواست", name: "requestDateStr" },
                                        { title: "نوع درخواست", name: "requestTypeName" },
                                        { title: "وضعیت", name: "statusName" },
                                        { title: "کد خطا", name: "errorTrackingCode" },
                                ]}
                                badgeOnCells={[
                                    {
                                        fieldName: "statusName",
                                        color: (x) => {
                                        switch (x) {
                                            case Constants.RequestState.Registerd.Title:
                                                return "gray"
                                            case Constants.RequestState.InProcess.Title:
                                                return "yellow";
                                            case Constants.RequestState.Done.Title:
                                                return "green";
                                            case Constants.RequestState.Error.Title:
                                                return "red";
                                        }}}]}
                            filterOptions={[
                                    { type: 'auto-complete', name: 'personCode', title: 'پرسنل', lookupApiUrl: "/api/amls/DistributionOfIndicator/SearchPersonels" },
                                    { type: "datepicker", name: "startDate", title: "از تاریخ", useEpoch: true },
                                    { type: "datepicker", name: "endDate", title: "تا تاریخ", useEpoch: true }
                            ]}
                            moreInfoLinks={[
                                    { title:'صورتحساب شخص', icon:'receipt-2',url: GetUrl('personBillingListPage/#replaceWith_id#') },
                                    { title:'دانلود فایل صورتحساب', icon:'receipt-2', func: this.downloadBillings, url: "" },
                                    { title:'مختومه', icon:'row-remove', url: "", enable: this.showRemoveRequest, func: this.closeRequest },
                                    { title:'پیام سیستم', icon:'message', func: this.showMessage, url: "", divider: true, enable: this.showHideMessage }
                                    ]}
                            />
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                                بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}
