import React from "react";
import { PageMain, Helpers,Card ,TextBox,DatePicker,DropDown} from "core";
import NotificationSystem from "react-notification-system";
import { jobOptions } from "../../utils/selectOptionsUtils";

class DetailsActivityPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        tabNumber: 0,
        isLoading: false,
        updateKey: 1,
      };
      this.tabNumber = props.tabNumber;
      this.pageInfo = {
       title:  "جزئیات فعالیت مشکوک",
        icon: "circle-plus",
      };
      this.notificationSystem = React.createRef();
    }
    render() {
      return (
        <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
         hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
            <TextBox
              context={this}
              lgCol={12}
              mdCol={12}
              name="age"
              title="گزارش بر اساس کدام قواعد (کمی - کیفی) ارسال"
              notificationSystem={this.notificationSystem}
              required
            />
        <TextBox
              context={this}
              lgCol={12}
              mdCol={12}
              name="age"
              title="گزارش مشکوک در کدام حساب اتفاق افتاده است؟"
              notificationSystem={this.notificationSystem}
              required
            />
        <DatePicker 
        context={ this } 
        name="expirationDateFrom"
         title="چه زمانی فعالیت مشکوک اتفاق افتاده است؟" 
         useEpoch 
         required />
     <TextBox
              context={this}
              lgCol={12}
              mdCol={12}
              name="age"
              title="مانده حساب"
              notificationSystem={this.notificationSystem}
              required
            />
                   <DropDown
            context={this}
            lgCol={6}
            mdCol={12}
            title="اگر حساب بسته / مسدود شده است تاریخ بسته / مسدود شدن حساب ذکر گردد"
            name="group"
            notificationSystem={this.notificationSystem}
            options={jobOptions()}
            required
          />
        <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="اگر حساب بسته / مسدود شده است تاریخ بسته / مسدود شدن حساب ذکر گردد"
              notificationSystem={this.notificationSystem}
              required
            />
                   <DatePicker 
        context={ this } 
        name="expirationDateFrom"
         title="تاریخ ارسال گزارش به مرکز" 
         useEpoch 
         required />
        </Card>
        
          <NotificationSystem ref={this.notificationSystem} />
        </>
      );
    }
  }
  
  export default DetailsActivityPage ;
