import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";
import Constants from '../../utils/constants'

export default class PersonInfListPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            dropDowns: null
        };

        this.notificationSystem = React.createRef();

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/amls/PersonInfo/GetAddPageData',
          { },
          successfulResponse =>
          {
              var temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonStateId);
              var states = (temp == null || temp.length <= 0 ? [] : temp[0].values);

              temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonTypeId);
              var personTypes = (temp == null || temp.length <= 0 ? [] : temp[0].values);;

              temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonNationalityId);
              var personNationalities = (temp == null || temp.length <= 0 ? [] : temp[0].values);;

              this.setState({
                  dropDowns: {
                      states: states,
                      personTypes: personTypes,
                      personNationalities: personNationalities
                  }
              })
          },
          errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
          () => { this.setState({ isLoading: false }) }, 'GET');
  }

  Backward(e)
  {
    e.preventDefault();
    history.back();
  }

  onDeleteErrorFunc(errorResponse)
  {
      return HandleError(Helpers, this.notificationSystem, errorResponse);
  }

  render()
  {
    return (
        <>
            <PageMain context={ this } title="لیست اشخاص " icon="users" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
            topButtons={[
                {
                title: "افزودن اطلاعات شخص",
                url: GetUrl("personInfAddPage"),
                color: "outline-primary",
                icon: "user-plus",
                },
            ]}>
            <div className="card">
                <div className="card-body">
                    <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } hideExportButton
                        filterBgColor="primary" name="name" title="فهرست گروه" notificationSystem={ this.notificationSystem } enableAutoFetch
                        listApiUrl="/api/amls/PersonInfo/SearchPersons" editPageUrl={ GetUrl("personInfEditPage" ) }
                        deleteApiUrl="/api/amls/PersonInfo/DeletePersonInfo" onDeleteErrorFunc={ this.onDeleteErrorFunc }
                        headers={[
                            { title: "نام", name: "name" },
                            { title: "کد", name: "code" },
                            { title: "تاریخ اعتبار از", name: "expirationDateFromStr" },
                            { title: "وضعیت", name: "statusName" },
                            { title: "نوع شخص", name: "personTypeName" },
                            { title: "تابعیت", name: "personNationalityName" }
                        ]}
                        filterOptions={[
                            { type: 'text', name: 'code', title: 'کد' },
                            //{ type: 'select', name: 'requestFormStateId', title: 'وضعیت', selectOptions: this.GetFormStates() }
                        ]} />
                </div>
                <div className="card-footer">
                    <div className="d-flex">
                        <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                        بازگشت
                        <i className="ti ti-arrow-left ms-1"></i>
                        </a>
                    </div>
                    </div>
                </div>
            </PageMain>
            <NotificationSystem ref={ this.notificationSystem } />
        </>
    );
  }
}
