import React from "react";
import { PageMain, Helpers, Card, TextBox, DatePicker } from "core";
import NotificationSystem from "react-notification-system";

class PosDevicePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title: "(دستگاه POS) ابزار پذیرش",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره حساب"
            notificationSystem={this.notificationSystem}
            required
          />
     <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="صنف مربوطه"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="نام PSP"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="تلفن پذیرنده"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="کد پایانه"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شهر پذیرنده"
            notificationSystem={this.notificationSystem}
            required
          />

          <DatePicker
            context={this}
            lgCol={6}
            mdCol={12}
            name="expirationDateFrom"
            title="تاریخ نصب"
            useEpoch
            required />

          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="کد پستی پذیرنده"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="نام پذیرنده"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="آدرس پذبرنده"
            notificationSystem={this.notificationSystem}
            required
          />
        </Card>

        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default PosDevicePage;
