import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";
import Constants from '../../utils/constants'
import { Backward } from "../../utils/generals";

export default class RegionInfoListPage extends Component {
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false,
            regionTypes: null
        };

        this.notificationSystem = React.createRef();

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/RegionInfo/GetRegionInfoListPageData',
            { },
            successfulResponse =>
            {
                this.setState({
                    regionTypes: successfulResponse.data.regionTypes,
                    provinces: successfulResponse.data.provinces,
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    onDeleteErrorFunc(errorResponse)
    {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
    }

    render()
    {
        return (
            <>
                <PageMain context={ this } title="لیست مناطق " icon="list-search" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                    title: "افزودن منطقه",
                    url: GetUrl("regionInfoAddPage"),
                    color: "outline-primary",
                    icon: "building",
                    },
                ]}>
                {
                    this.state.regionTypes &&
                    <div className="card">
                    <div className="card-body">
                        <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                            filterBgColor="primary" name="crdRegionList" title="لیست مناطق" notificationSystem={ this.notificationSystem }
                            enableAutoFetch listApiUrl="/api/amls/RegionInfo/SearchRegionsInfo" editPageUrl={ GetUrl("RegionInfoEditPage" ) }
                            deleteApiUrl="/api/amls/RegionInfo/DeleteRegionInfo" onDeleteErrorFunc={ this.onDeleteErrorFunc }
                            headers={[
                                { title: "نوع منطقه", name: "regionTypeName" },
                                { title: "کد استان", name: "provinceCode" },
                                { title: "نام استان", name: "provinceName" },
                                { title: "کد شهر", name: "cityCode" },
                                { title: "نام شهر", name: "cityName" },
                                { title: "وضعیت", name: "isActive", type: "bool" }
                            ]}
                            filterOptions={[
                                { type: 'select', name: 'regionTypeId', title: 'نوع منطقه', selectOptions: this.state.regionTypes },
                                { type: 'select', name: 'provinceCode', title: 'استان', selectOptions: this.state.provinces },
                                { type: 'auto-complete', name: 'cityCode', title: 'شهر', lookupApiUrl: "/api/amls/RegionInfo/SearchCities" },
                            ]} />
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => Backward(e) } href="/" title="بازگشت به صفحه قبل">
                                بازگشت
                                <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                        </div>
                    </div>
                }
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        )
    }
}
