import { Component } from "react";
import React from "react";
import { Helpers, PageMain, Card, TextBox, DropDown, DatePicker, FileUploader } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from '../../../utils/generals'
import Constants from "../../../utils/constants";

export default class RequestBillingAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false,
            updateKey: 1,
            searchMultiPerson: true
        };

        this.notificationSystem = React.createRef();

        this.personNumberTypes = [
            Constants.PersonNumberType.Real,
            Constants.PersonNumberType.Legal,
            Constants.PersonNumberType.Foreign
        ];
        this.personNumber = {
            Title: null,
            MaxLength: null
        };

        this.onPersonNumberTypeSelected = this.onPersonNumberTypeSelected.bind(this);
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);


        this.onPersonClicked = this.onPersonClicked.bind(this);
        this.onMultiPersonClicked = this.onMultiPersonClicked.bind(this);
    }

    // Events.
    onPersonNumberTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.personNumber = {
                Title: null,
                MaxLength: null
            };
            return;
        }
        switch(item.value)
        {
            case Constants.PersonNumberType.Real.value:
                this.personNumber = {
                    Title: "کد ملی",
                    MaxLength: 10
                };
            break;
            case Constants.PersonNumberType.Legal.value:
                this.personNumber = {
                    Title: "شناسه ملی",
                    MaxLength: 11
                };
            break;
            case Constants.PersonNumberType.Foreign.value:
                this.personNumber = {
                    Title: "کد فراگیر اتباع",
                    MaxLength: 12
                };
            break;
            default:
                this.personNumber = null;
            break;
        }
    }

    onBeforeSubmit()
    {
        if(this.state.searchMultiPerson)
        {

            return true;
        }
        else
        {
            var customerNumber = this.state.customerNumber;
            var number = this.state.number;
    
            if((customerNumber == null || customerNumber == '') && (number == null || number == ''))
            {
                Helpers.DisplayErrorMessage(this.notificationSystem, null, 'حداقل یکی از پارامترهای شماره مشتری/کد ملی/شناسه ملی و یا کد فراگیر اتباع را وارد نمایید');
                return false;
            }
            return true;
        }
    }

    onSubmitSuccess(response)
    {
        this.setState({
            customerNumber: null,
            personNumberTypeId: null,
            number: null,
            accountNumber: null,
            cardNumber: null,
            transactionFromDate: null,
            transactionToDate: null,
            updateKey: this.state.updateKey + 1
        });
        Helpers.DisplaySuccessMessage(this.notificationSystem, response.message);
    }

    onPersonClicked()
    {
        this.setState({
            searchMultiPerson: false
        });
    }

    onMultiPersonClicked()
    {
        this.setState({
            searchMultiPerson: true
        });
    }

    render()
    {
        return (
            <>
                <PageMain title="افزودن درخواست صورتحساب" icon="receipt" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                    topButtons={[
                        {
                            title: "لیست درخواست دانلود صورتحساب",
                            url: GetUrl("requestBillingListPage"),
                            color: "outline-primary",
                            icon: "list-search",
                        }
                    ]}>
                        <Card context={ this } title="جزئیات افزودن درخواست صورتحساب" name="crdRequestBilling" statusColor="primary" hasDefaultSaveButton
                            submitUrl="/api/amls/Request/AddBillingRequest" hasBackButton icon="receipt" onBeforeSubmit={ this.onBeforeSubmit } updateKey={ this.state.updateKey }
                            onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} onSubmitSuccess={ this.onSubmitSuccess }>
                                
                                <ul class="nav nav-tabs">
                                    <li class="nav-item">
                                        <a class="nav-link active" aria-current="page" onClick={ this.onPersonClicked }>صورتحساب تکی</a>
                                    </li>
                                    <li class="nav-item">
                                        <a class="nav-link" href="#">صورتحساب گروهی</a>
                                    </li>
                                </ul>

                                {
                                    this.state.searchMultiPerson == true
                                        ? <FileUploader context={ this } name="ful" title="امضا" validExtensions={['.jpg','.gif','.png','.jpeg']} />
                                        :
                                        <>
                                            <TextBox context={ this } name="customerNumber" title="شماره مشتری" minLength={ 1 } maxLength={ 25 } />

                                            <DropDown context={ this } name="personNumberTypeId" title="نوع شخص" options={ this.personNumberTypes } changeHandler={ this.onPersonNumberTypeSelected } />
                                            <TextBox context={ this } name="number" title={ this.personNumber.Title == null ? "شماره" : this.personNumber.Title } minLength={ 1 }
                                                maxLength={ this.personNumber.MaxLength == null ? 1 : this.personNumber.MaxLength } readOnly={ this.personNumber.Title == null }/>

                                            <TextBox context={ this } name="accountNumber" title="شماره حساب" minLength={ 1 } maxLength={ 25 } />
                                            <TextBox context={ this } name="cardNumber" title="شماره کارت" minLength={ 1 } maxLength={ 16 } />

                                            {/* <DatePicker context={ this } name="requestDate" title="تاریخ درخواست" useEpoch /> */}

                                            <DatePicker context={ this } name="transactionFromDate" title="از تاریخ تراکنش" useEpoch required />
                                            <DatePicker context={ this } name="transactionToDate" title="تا تاریخ تراکنش" useEpoch required />
                                        </>
                                }                                
                        </Card>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
