import React from "react";
import { PageMain, Helpers,Card ,TextBox,DatePicker,DropDown} from "core";
import NotificationSystem from "react-notification-system";
import { yesNoOptions,almsOptions } from "../../utils/selectOptionsUtils";

class DecriptionReportPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        tabNumber: 0,
        isLoading: false,
        updateKey: 1,
      };
      this.tabNumber = props.tabNumber;
      this.pageInfo = {
       title:   "شرح گزارش",
        icon: "circle-plus",
      };
      this.notificationSystem = React.createRef();
    }
    render() {
      return (
        <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
         hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
         
        <TextBox
              context={this}
              lgCol={12}
              mdCol={12}
              name="age"
              title="تحلیل و نتیجه گیری"
              notificationSystem={this.notificationSystem}
              required
            />
        </Card>
        
          <NotificationSystem ref={this.notificationSystem} />
        </>
      );
    }
  }
  
  export default DecriptionReportPage ;
