import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import PersonInfSection from '../../sections/personInfSection'
import { GetUrl, HandleError } from "../../utils/generals";

export default class PersonInfEditPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            record: null,
            dropDowns: null,
            isLoading: true
        };

        this.notificationSystem = React.createRef();

        this.personInfId = this.props.match.params.personInfId;
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/PersonInfo/GetEditPageData/' + this.personInfId,
            { },
            successfulResponse =>
            {
                var temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonStateId);
                var states = (temp == null || temp.length <= 0 ? [] : temp[0].values);

                temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonTypeId);
                var personTypes = (temp == null || temp.length <= 0 ? [] : temp[0].values);;

                temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonNationalityId);
                var personNationalities = (temp == null || temp.length <= 0 ? [] : temp[0].values);;
                
                this.setState({
                    record: successfulResponse.data.personInfo,
                    dropDowns: {
                        states: states,
                        personTypes: personTypes,
                        personNationalities: personNationalities
                    }
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (this.state.dropDowns != null && this.state.record != null) && (
            <>
                <PageMain title="ویرایش اطلاعات شخص" icon="user-check" footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                        title: "لیست اشخاص",
                        url: GetUrl("personInfListPage"),
                        color: "outline-primary",
                        icon: "users",
                    },
                ]}>
                <PersonInfSection record={ this.state.record } dropDowns={ this.state.dropDowns } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
