import { Component } from "react";
import React from "react";
import { Card, DropDown, Helpers, DatePicker, TextBox, Delimiter, AutoComplete } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"
import Constants from "../utils/constants"
import '../assets/app.css'

export default class CustomerActivityLevelSection extends Component
{
    constructor(props)
    {
        var state = {
            controls: null,
            jobState: true,
            updateKey: 1
        };

        super(props);

        if(this.props.record != null)
        {
            state['expirationDateFrom'] = this.props.record.expirationDateFrom;
            state['allowedActivityLevel'] = this.props.record.allowedActivityLevel;
            state['allowedLevelAboveActivityLevelPercent'] = this.props.record.allowedLevelAboveActivityLevelPercent;
            state['personTypeId'] = this.props.record.personTypeId.toString();
            state['jobTypeId'] = this.props.record.jobTypeId == null ? this.props.record.jobTypeId : this.props.record.jobTypeId.toString();
            state['jobId'] = this.props.record.jobId;
            state['taxStateId'] = this.props.record.taxStateId == null ? this.props.record.taxStateId : this.props.record.taxStateId.toString();
            state['guildId'] = this.props.record.guildId;
            state['id'] = this.props.record.id;
        }

        this.state = state;

        this.notificationSystem = React.createRef();

        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
    }

    // Events.
    onBeforeSubmit()
    {
        var personTypeId = this.state.personTypeId;

        switch(personTypeId)
        {
            case Constants.PersonTypes.Real:
                var jobTypeId = this.state.jobTypeId;

                if(jobTypeId == null)
                {
                    Helpers.DisplayErrorMessage(this.notificationSystem, null, 'وضعیت اشتغال را وارد نمایید');
                    return false;
                }
                if(jobTypeId == Constants.JobType.Employed && (this.state.jobId == null || this.state.jobId == ''))
                {
                    Helpers.DisplayErrorMessage(this.notificationSystem, null, 'شغل را وارد نمایید');
                    return false;
                }
            break;
            case Constants.PersonTypes.Legal:
                var taxStateId = this.state.taxStateId;

                if(taxStateId == null)
                {
                    Helpers.DisplayErrorMessage(this.notificationSystem, null, 'وضعیت مالیات را وارد نمایید');
                    return false;
                }
                if(taxStateId == Constants.TaxState.TaxActivist && (this.state.guildId == null || this.state.guildId == ''))
                {
                    Helpers.DisplayErrorMessage(this.notificationSystem, null, 'صنف را وارد نمایید');
                    return false
                }
            break;
        }
        return true;
    }

    onSubmitSuccess(response)
    {
        if(this.props.record == null)
            this.setState({
                expirationDateFrom: null,
                allowedActivityLevel: null,
                allowedLevelAboveActivityLevelPercent: null,
                personTypeId: null,
                jobTypeId: null,
                jobId: null,
                taxStateId: null,
                guildId: null,
                updateKey: this.state.updateKey + 1
            });

        Helpers.DisplaySuccessMessage(this.notificationSystem, response.message);
    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن سطح فعالیت مشتری" : "جزئیات ویرایش سطح فعالیت مشتری" } name="crdCustomer"
                    statusColor="primary" hasDefaultSaveButton updateKey={ this.state.updateKey } onBeforeSubmit={ this.onBeforeSubmit } onSubmitSuccess={ this.onSubmitSuccess }
                    submitUrl={ this.props.record == null ? "/api/amls/CustomerActivityLevel/AddCustomerActivityLevel" : "/api/amls/CustomerActivityLevel/UpdateCustomerActivityLevel" }
                    hasBackButton icon="user-circle" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}>

                    <DatePicker context={ this } name="expirationDateFrom" title="تاریخ اعتبار از" useEpoch required />
                    <TextBox context={ this } name="allowedActivityLevel" title="سطح فعالیت مجاز" required commaSeparated minLength={ 1 } maxLength={ 15 }
                        validateHandler={[ Helpers.NumberTypeValidator, Helpers.MinMaxLengthValidator ]} leftText="ریال" />
                    <TextBox context={ this } name="allowedLevelAboveActivityLevelPercent" title="سطح مجاز بالاتر از سطح فعالیت" minLength={ 1 } maxLength={ 3 } required
                        validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]} leftIcon="percentage" />
                    <DropDown context={ this } name="personTypeId" title="نوع شخص" options={ this.props.dropDowns.personTypes } required />
                    <div className={ "row" + (this.state.personTypeId == Constants.PersonTypes.Real ? "" : " Hide") }>
                    <Delimiter></Delimiter>
                        <DropDown context={ this } name="jobTypeId" title="وضعیت اشتغال" options={ this.props.dropDowns.jobTypes } lgCol={ 4 } mdCol={ 4 } />
                        {/* ToDo: Add Code here for field jobId */}
                        <AutoComplete context={ this } name="jobId" title="شغل" notificationSystem={ this.notificationSystem } lgCol={ 4 } mdCol={ 4 }
                            remoteUrl="/api/amls//" disabled={ this.state.jobTypeId != Constants.JobType.Employed }
                            externalData={ this.props.record == null
                                ? [{ label: 'برنامه نویس', value: '4c2d663a-e6b6-4d6b-a2cd-3918f978d195' }]
                                : [{ label: 'برنامه نویس', value: '4c2d663a-e6b6-4d6b-a2cd-3918f978d195' }
                            ]} />
                    </div>
                    <div className={ "row" + (this.state.personTypeId == Constants.PersonTypes.Legal ? "" : " Hide") }>
                        <Delimiter></Delimiter>
                        <DropDown context={ this } name="taxStateId" title="وضعیت مالیات" options={ this.props.dropDowns.taxStates } lgCol={ 4 } mdCol={ 4 }/>
                        {/* ToDo: Add Code here for field guildId */}
                        <AutoComplete context={ this } name="guildId" title="صنف" notificationSystem={ this.notificationSystem } 
                            remoteUrl="/api/amls//" lgCol={ 4 } mdCol={ 4 } disabled={ this.state.taxStateId != Constants.TaxState.TaxActivist }
                            externalData={ this.props.record == null
                                ? [{ label: 'IT', value: '4f06a01b-e055-44ab-805c-29be577b6e30' }]
                                : [{ label: 'IT', value: '4f06a01b-e055-44ab-805c-29be577b6e30' }
                            ]} />
                    </div>
                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
