import { Component } from "react";
import React from "react";
import { Helpers, PageMain, Card, TextBox, DropDown, DatePicker } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from '../../utils/generals'
import Constants from "../../utils/constants";
import { IsRealPersonNationalIdValid, IsLegalPersonNationalIdValid } from '../../utils/nationalIdentification'

export default class RegisterStrAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false,
            updateKey: 1
        };

        this.notificationSystem = React.createRef();

        this.personNumberTypes = [
            Constants.PersonNumberType.Real,
            Constants.PersonNumberType.Legal,
            Constants.PersonNumberType.Foreign
        ];
        this.personNumber = {
            Title: null,
            MaxLength: null
        };

        this.onPersonNumberTypeSelected = this.onPersonNumberTypeSelected.bind(this);
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
    }

    // Events.
    onPersonNumberTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.personNumber = {
                Title: null,
                MaxLength: null
            };
            return;
        }
        switch(item.value)
        {
            case Constants.PersonNumberType.Real.value:
                this.personNumber = {
                    Title: "کد ملی",
                    MaxLength: 10
                };
            break;
            case Constants.PersonNumberType.Legal.value:
                this.personNumber = {
                    Title: "شناسه ملی",
                    MaxLength: 11
                };
            break;
            case Constants.PersonNumberType.Foreign.value:
                this.personNumber = {
                    Title: "کد فراگیر اتباع",
                    MaxLength: 12
                };
            break;
            default:
                this.personNumber = null;
            break;
        }
    }

    onBeforeSubmit()
    {
        var number = this.state.number;

        if(number != null || number != '')
            switch(this.state.personNumberTypeId)
            {
                case Constants.PersonNumberType.Real.value:
                    if(IsRealPersonNationalIdValid(number) == false)
                    {
                        Helpers.DisplayErrorMessage(this.notificationSystem, null, 'کد ملی صحیح نمی باشد');
                        return false;
                    }
                break;
                case Constants.PersonNumberType.Legal.value:
                    if(IsLegalPersonNationalIdValid(number) == false)
                        {
                            Helpers.DisplayErrorMessage(this.notificationSystem, null, 'شناسه ملی صحیح نمی باشد');
                            return false;
                        }
                break;
            }
        return true;
    }

    onSubmitSuccess(response)
    {
        this.setState({
            customerNumber: null,
            personNumberTypeId: null,
            number: null,
            toReportDate: null,
            updateKey: this.state.updateKey + 1
        });
        Helpers.DisplaySuccessMessage(this.notificationSystem, response.message);
    }

    render()
    {
        return (
            <>
                <PageMain title="ثبت درخواست STR پولشویی" icon="license" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                    topButtons={[
                        {
                            title: "فهرست درخواست ثبت STR پولشویی",
                            url: GetUrl("registerStrListPage"),
                            color: "outline-primary",
                            icon: "list-search",
                        }
                    ]}>
                        <Card context={ this } title="جزئیات ثبت درخواست STR پولشویی" name="crdRegisterStr" statusColor="primary" hasDefaultSaveButton
                            submitUrl="/api/amls/Request/AddRegisterStrRequest" hasBackButton icon="license" onBeforeSubmit={ this.onBeforeSubmit } updateKey={ this.state.updateKey }
                            onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} onSubmitSuccess={ this.onSubmitSuccess }>

                                <TextBox context={ this } name="customerNumber" title="شماره مشتری" minLength={ 1 } maxLength={ 25 } />

                                <DropDown context={ this } name="personNumberTypeId" title="نوع شخص" options={ this.personNumberTypes } changeHandler={ this.onPersonNumberTypeSelected } />
                                <TextBox context={ this } name="number" title={ this.personNumber.Title == null ? "شماره" : this.personNumber.Title } minLength={ 1 }
                                    maxLength={ this.personNumber.MaxLength == null ? 1 : this.personNumber.MaxLength } readOnly={ this.personNumber.Title == null }/>

                                <DatePicker context={ this } name="toReportDate" title="تاریخ گزارش تا" useEpoch />

                        </Card>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
