import { Component } from "react";
import React from "react";
import { CheckBox, Card, TextBox, Helpers, AutoComplete, Label, Delimiter, DatePicker, AlertBox } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";
import { IsLegalPersonNationalIdValid } from '../../utils/nationalIdentification'
import Constants from '../../utils/constants'

export default class LegalCompanyInfoSection extends Component {
    constructor(props) {
      super(props);
      
      if(this.props.formData == null)
        this.state = {
          hasSaveButton: true,
          updateKey: 1,
          branchCode: (this.props.personnelBranchInfo == null || this.props.personnelBranchInfo.personnelUnit == null ? null : this.props.personnelBranchInfo.personnelUnit.code)
        };
      else
        this.state = {
          nationalIdStr: this.props.formData.nationalId.toString(),
          companyName: this.props.formData.companyName,
          customerNumber: this.props.formData.customerNumber,
          accountNumber: this.props.formData.accountNumber,
          branchCode: this.props.formData.branchCode.toString(),
          isSalaryPaymentOnSepahBank: this.props.formData.isSalaryPaymentOnSepahBank,
          hasSaveButton: true,
          loadingCustomer: false,
          updateKey: 1,
          accountOpeningDate: this.props.formData.accountOpeningDate,
          contractId: this.props.formData.contractId
        };

        this.notificationSystem = React.createRef();
        this.companyInfoId = null;

        this.onBranchSelected = this.onBranchSelected.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.OnGetCustomerInfo = this.OnGetCustomerInfo.bind(this);
        this.IsValid = this.IsValid.bind(this);
        this.GetExtraDataToSend = this.GetExtraDataToSend.bind(this);
        this.GetBranchInfo = this.GetBranchInfo.bind(this);
        this.IsEditMode = this.IsEditMode.bind(this);
    }
  
  IsValid(contorlName) {
    if (this.state[contorlName + "IsValid"] == false) {
      Helpers.DisplayErrorMessage(this.notificationSystem, null, this.state[contorlName + "ValidFeedBack"]);
      return false;
    }
    return true;
  }

  GetExtraDataToSend()
  {
    if(this.props.formData == null)
      return { LegalCompanyInfoId: this.companyInfoId };
    else
      return {
        CompanyLoanAccountId: this.props.formData.companyLoanAccountId,
        LegalCompanyInfoId: this.props.formData.legalCompanyInfoId
      };
  }

  // Events.
  onBranchSelected(item)
  {
    if(item == null || item.value == null || item.value == '')
    {
      Helpers.DisplayWarningMessage(this.notificationSystem, null, 'اطلاعات شعبه پیدا نشد');
      return;
    }
    Helpers.CallServer('/api/FRQST/LegalCompanyInfo/GetRegion/' + item.value,
    { },
    successfulResponse =>
    {
      if(successfulResponse.data == null)
      {
        this.setState({
          region: null,
          regionCode: null
        });        Helpers.DisplayErrorMessage(this.notificationSystem, null, 'اطلاعات مربوط به منطقه شعبه پیدا نشد');
        return;
      }
      this.setState({
        region: successfulResponse.data,
        regionCode: successfulResponse.data.code
      });
    },
    errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
    () => { }, 'GET');
  }

  onSubmitSuccess(response)
  {
    if(this.props.formData == null)
    {
      this.setState({ hasSaveButton: false })
      window.open(GetUrl("companyLoanInfoPage/" + response.data.data), '_self');
    }
    else
      Helpers.DisplaySuccessMessage(this.notificationSystem, response.data.message);
  }

  onBeforeSubmit()
  {
    if(IsLegalPersonNationalIdValid(this.state.nationalIdStr) == false)
    {
      Helpers.DisplayErrorMessage(this.notificationSystem, null, 'شناسه ملی معتبر نمی باشد');
      return false;
    }
    return true;
  }

  OnGetCustomerInfo()
  {
    if(this.IsValid('customerNumber') == false)
      return;
    var state = {
      updateKey: this.state.updateKey + 1,
      loadingCustomer: false
    };

    this.setState({ loadingCustomer: true });
    Helpers.CallServer('/api/FRQST/LegalCompanyInfo/GetByCustomerNumber/' + this.state.customerNumber,
    { },
    successfulResponse =>
    {
        this.companyInfoId = successfulResponse.data.id;
        state['nationalIdStr'] = successfulResponse.data.nationalId.toString();
        state['companyName'] = successfulResponse.data.companyName;
        Helpers.DisplaySuccessMessage(this.notificationSystem, successfulResponse.message);
    },
    errorResponse =>
    {
      this.companyInfoId = null;
      state['nationalIdStr'] = null;
      state['companyName'] = null;
      return HandleError(Helpers, this.notificationSystem, errorResponse);
    },
    () => this.setState(state), 'GET')
  }

  GetBranchInfo()
  {
    if(this.props.formData == null)
    {
      if(this.props.personnelBranchInfo == null || this.props.personnelBranchInfo.personnelUnit == null)
        return null;
      return [{ label: this.props.personnelBranchInfo.personnelUnit.name, value: this.props.personnelBranchInfo.personnelUnit.code }];
    }
    else
      return [{ label: this.props.formData.branchName, value: this.props.formData.branchCode.toString() }];
  }

  IsEditMode()
  {
    return Helpers.IsInRole(Constants.Roles.ProjectBranchUser) == true && this.props.formData != null;
  }

  render()
  {
    return (
        <>
        <Card context={ this } title="جزئیات اطلاعات شرکت" icon="building-community" hasBackButton
          submitUrl={ this.props.formData == null
                      ? "/api/FRQST/LegalCompanyInfo/AddCompanyInfo"
                      : "/api/FRQST/LegalCompanyInfo/ReviseCompanyInfo" } updateKey={ this.state.updateKey }
          statusColor="success" hasDefaultSaveButton={ this.state.hasSaveButton } onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}
          onSubmitSuccess={ this.onSubmitSuccess } onBeforeSubmit={ this.onBeforeSubmit }
          extraDataToSend={ this.GetExtraDataToSend() }>
            {
              this.props.formData == null
              ? <AlertBox context={ this } type="warning" hasIcon>مبنای محاسبه میانگین حساب تاریخ افتتاح حساب می باشد</AlertBox>
              : null
            }
            <TextBox context={ this } name="customerNumber" title="شماره مشتری" maxLength={ 10 } validateHandler={ Helpers.NumberTypeValidator } required 
              leftButton={ this.props.formData == null
                ? {icon:'users', text:'استعلام',  color:'info', onclick: this.OnGetCustomerInfo, loading: this.state.loadingCustomer }
                : null } readOnly={ this.IsEditMode() == true ? true : false } />
            <TextBox context={ this } name="nationalIdStr" title="شناسه ملی" maxLength={ 11 } validateHandler={ Helpers.NumberTypeValidator } required
              readOnly={ this.IsEditMode() == true ? true : false } />
            <TextBox context={ this } name="companyName" title="نام شرکت / نهاد / سازمان" maxLength={ 64 } required
              readOnly={ this.IsEditMode() == true ? true : false } />
            <Delimiter></Delimiter>
            <TextBox context={ this } name="contractId" title="شماره تفاهم نامه" maxLength={ 15 } minLength={ 1 } validateHandler={ Helpers.MinMaxLengthValidator } required />
            <TextBox context={ this } name="accountNumber" title="شماره حساب" maxLength={ 20 } validateHandler={ Helpers.NumberTypeValidator } required
                     readOnly={ this.props.formData == null ? false : true } />
            <DatePicker context={ this } name="accountOpeningDate" title="تاریخ افتتاح حساب" useEpoch required readOnly={ this.props.formData == null ? false : true } />
            <AutoComplete context={ this } name="branchCode" title="شعبه" notificationSystem={ this.notificationSystem } required
                remoteUrl="/api/FRQST/LegalCustomer/GetBranches" changeHandler={ this.onBranchSelected } externalData={ this.GetBranchInfo() }
                disabled={ this.props.personnelBranchInfo == null || this.props.personnelBranchInfo.personnelUnit == null ? false : true } />
            <Label context={ this } name="regionCode" title="منطقه" value={ this.state.region == null ? 'نامشخص' : '(' + this.state.region.code + ') - ' + this.state.region.name } />
            <CheckBox context={ this } name="isSalaryPaymentOnSepahBank" title="دریافت حقوق پرسنل از طریق بانک سپه" required disabled={ this.props.formData == null ? false : true } />
        </Card>
        <NotificationSystem ref={ this.notificationSystem } />
      </>
    );
  }
}
