import { Component } from "react";
import React from "react";
import NotificationSystem from "react-notification-system";
import { SimpleGrid } from "core";
import Constants from '../utils/constants';
import { GetUrl } from "../utils/generals";

export default class ManagementOfSuspiciousTransactionsAndOperationSection extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {};
        this.notificationSystem = React.createRef();
    }

    // Events.
    render()
    {
        return (
            <>
                 context={ this } dataKey="nationalNumber" minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                    filterBgColor="primary" name="crdsusList" title="جزئیات مدیریت معاملات و عملیات مشکوک" notificationSystem={ this.notificationSystem }
                    enableAutoFetch listApiUrl="/api/amls/bi/RolesResult/SearchRolesResult" disabledPaging={ this.props.showOptions ? false : true }
                    headers={[
                        { title: "نام مشتری", name: "customerName" },
                        { title: "شماره مشتری", name: "customerNumber" },
                        { title: "کد ملی/ شناسه ملی/کد فراگیر اتباع", name: "nationalNumber" },
                        { title: "شاخص های پیدا شده", name: "foundRoles" },
                        { title: "تعداد شاخص ها پیدا شده", name: "foundRolesCount" },
                    ]}
                    filterOptions={ this.props.showOptions
                        ? [
                            { type: 'text', name: 'customerNumber', title: 'شماره مشتری' },
                            { type: 'text', name: 'nationalNumber', title: 'کد ملی/ شناسه ملی/کد فراگیر اتباع' },
                          ]
                        : null }
                    moreInfoLinks={this.props.showOptions
                        ? [
                            { title:'دریافت صورتحساب', icon:'receipt-2',url: GetUrl('receiveInvoiceListPage/#replaceWith_nationalNumber#') },
                            { title:'دریافت CTR', icon:'mist', url: GetUrl('receiveCTRListPage/#replaceWith_nationalNumber#') },
                            { title:'دریافت STR', icon:'notes', url: GetUrl('receiveSTRListPage/#replaceWith_nationalNumber#') },
                            { title:'ایجاد گزارش تکمیلی', icon:'playlist-add', url: GetUrl('additionalReviewPage/#replaceWith_nationalNumber#') }

                            // { title:'ایجاد STR اولیه', icon:'playlist-add', url: GetUrl('createSTRPrimaryListPage/#replaceWith_nationalNumber#') },
                            // { title:'بررسی تکمیلی', icon:'playlist-add', url: GetUrl('createSTRPrimaryListPage/#replaceWith_nationalNumber#') }
                          ]
                        : [] } />
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
