import { Component } from "react";
import React from "react";
import { Card, DropDown, Helpers, DatePicker, TextBox, Delimiter, AutoComplete, CellContainer } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"
import Constants from "../utils/constants"
import '../assets/app.css'

export default class CustomerActivityLevelSection extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            controls: null,
            jobState: true,
            updateKey: 1
        };

        this.notificationSystem = React.createRef();

        this.onPersonTypeSelected = this.onPersonTypeSelected.bind(this);
        this.onJobTypeSelected = this.onJobTypeSelected.bind(this);
    }

    GetRealPersonControls()
    {
        var controls  = [];

        controls.push(<DropDown key="r01" context={ this } name="jobTypeId" title="وضعیت اشتغال" options={ this.props.dropDowns.jobTypes } required changeHandler={ this.onJobTypeSelected } />);
        controls.push(<AutoComplete key="r02" context={ this } name="jobId" title="شغل" notificationSystem={ this.notificationSystem } required
                        remoteUrl="/api/amls//" disabled={ this.state.jobState }
                        externalData={ this.props.record == null
                            ? [{ label: 'برنامه نویس', value: '4c2d663a-e6b6-4d6b-a2cd-3918f978d195' }]
                            : [{ label: 'برنامه نویس', value: '4c2d663a-e6b6-4d6b-a2cd-3918f978d195' }
                        ]} />);

        return controls;
    }

    GetLegalPersonControls()
    {
        var controls  = [];

        controls.push(<DropDown key="l01" context={ this } name="taxStateId" title="وضعیت مالیات" options={ this.props.dropDowns.taxStates } required />);
        controls.push(<AutoComplete key="l02" context={ this } name="guildId" title="صنف" notificationSystem={ this.notificationSystem } required
                        remoteUrl="/api/amls//"
                        externalData={ this.props.record == null
                            ? [{ label: 'IT', value: '4f06a01b-e055-44ab-805c-29be577b6e30' }]
                            : [{ label: 'IT', value: '4f06a01b-e055-44ab-805c-29be577b6e30' }
                        ]} />);

        return controls;
    }

    // Events.
    onJobTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({ jobState: true });
            return;
        }
        var jobTypeId = item.value;

        console.log("jobTypeId: ", jobTypeId);
        console.log("jobState: ", jobTypeId != Constants.JobType.Employed);
        this.setState({
            jobState: jobTypeId != Constants.JobType.Employed,
            
        });
    }

    onPersonTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({ controls: null });
            return;
        }
        var personTypeId = item.value;
        var controls = null;

        switch(personTypeId)
        {
            case Constants.PersonTypes.Real:
                controls = this.GetRealPersonControls();
            break;
            case Constants.PersonTypes.Legal:
                controls = this.GetLegalPersonControls();
            break;
        }
        this.setState({
            controls: controls
        });
    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن سطح فعالیت مشتری" : "جزئیات ویرایش سطح فعالیت مشتری" } name="crdCustomer"
                    statusColor="primary" hasDefaultSaveButton updateKey={ this.state.updateKey } onBeforeSubmit={ this.onBeforeSubmit } onSubmitSuccess={ this.onSubmitSuccess }
                    submitUrl={ this.props.record == null ? "/api/amls/CustomerActivityLevel/AddCustomerActivityLevel" : "/api/amls/CustomerActivityLevel/UpdateCustomerActivityLevel" }
                    hasBackButton icon="user-circle" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}>

                    <DatePicker context={ this } name="expirationDateFrom" title="تاریخ اعتبار از" useEpoch required />
                    <TextBox context={ this } name="allowedActivityLevel" title="سطح فعالیت مجاز" required commaSeparated minLength={ 1 } maxLength={ 15 }
                        validateHandler={[ Helpers.NumberCommaSeparated, Helpers.NumberTypeValidator, Helpers.MinMaxLengthValidator ]} leftText="ریال" />
                    <TextBox context={ this } name="allowedLevelAboveActivityLevelPercent" title="سطح مجاز بالاتر از سطح فعالیت" minLength={ 1 } maxLength={ 3 } required
                        validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]} leftIcon="percentage" />
                    <DropDown context={ this } name="personTypeId" title="نوع شخص" options={ this.props.dropDowns.personTypes } required
                        changeHandler={ this.onPersonTypeSelected }  />
                    <Delimiter></Delimiter>
                    {/* { this.state.controls } */}
                    <CellContainer className="" lgCol={ 12 } mdCol={ 12 }>
                        <DropDown key="r01" context={ this } name="jobTypeId" title="وضعیت اشتغال" options={ this.props.dropDowns.jobTypes } required changeHandler={ this.onJobTypeSelected } />
                        <AutoComplete key="r02" context={ this } name="jobId" title="شغل" notificationSystem={ this.notificationSystem } required
                            remoteUrl="/api/amls//" disabled={ this.state.jobState }
                            externalData={ this.props.record == null
                                ? [{ label: 'برنامه نویس', value: '4c2d663a-e6b6-4d6b-a2cd-3918f978d195' }]
                                : [{ label: 'برنامه نویس', value: '4c2d663a-e6b6-4d6b-a2cd-3918f978d195' }
                            ]} />
                    </CellContainer>
                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
