import React from "react";
import { PageMain, Helpers,Card ,TextBox,DatePicker,DropDown} from "core";
import NotificationSystem from "react-notification-system";
import { yesNoOptions,almsOptions } from "../../utils/selectOptionsUtils";

class DetailsReportPage extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        tabNumber: 0,
        isLoading: false,
        updateKey: 1,
      };
      this.tabNumber = props.tabNumber;
      this.pageInfo = {
       title:  "جزئیات گزارش",
        icon: "circle-plus",
      };
      this.notificationSystem = React.createRef();
    }
    render() {
      return (
        <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
         hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
          <DropDown
            context={this}
            lgCol={12}
            mdCol={12}
            title="آیا گزارش با گزارشات قبلی ارتباط دارد یا شخص موضوع گزارش و نیز مرتبطین فرد مسبوق به سابقه می‌باشند؟"   
                     name="group"
            notificationSystem={this.notificationSystem}
            options={yesNoOptions()}
            required
          />
    <DropDown
            context={this}
            lgCol={12}
            mdCol={12}
            title="اعلام نظر واحد مبارزه با پولشویی در خصوص احتمال وقوع هریک از موارد ذکر شده(به استناد بند 2 ماده 38 آیین نامه اجرایی ماده 14 الحاقی قانون مبارزه با پولشویی)"
            name="group"
            notificationSystem={this.notificationSystem}
            options={almsOptions()}
            required
          />
        <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title=""
              notificationSystem={this.notificationSystem}
            />
        </Card>
        
          <NotificationSystem ref={this.notificationSystem} />
        </>
      );
    }
  }
  
  export default DetailsReportPage ;
