import { Helpers, PageMain, SimpleGrid, TextBox, DatePicker, DropDown, Delimiter } from "core";
import React, { Component } from "react";
import NotificationSystem from "react-notification-system";
import { IsLegalPersonNationalIdValid, IsRealPersonNationalIdValid } from "../../../utils/nationalIdentification";
import Constants from "../../../utils/constants"
import { Backward, GetUrl, HandleError } from "../../../utils/generals";

const btnWidth =
{
    "width": "90px"
}

export default class BranchStrListPage extends Component {
    constructor(props) {
        super(props);

        this.PersonTypes = [ Constants.PersonNumberType.Real, Constants.PersonNumberType.Legal, Constants.PersonNumberType.Foreign ];

        this.state = {
            isLoading: false,
            numberTitle: "کد ملی / شناسه ملی / کد فراگیر اتباع",
            numberMaxLength: 10,
            numberIsReadOnly: true,
            gridUpdate: 1,
            filterUpdate: 1
        };

        this.notificationSystem = React.createRef();

        this.searchStr = this.searchStr.bind(this);
        this.downLoadStr = this.downLoadStr.bind(this);
        this.resetFilters = this.resetFilters.bind(this);
    }

    onPersonNumberTypeIdChange = (e) => {
        let title = 'کد ملی / شناسه ملی / کد فراگیر اتباع';
        let length = 0;

        if (e.value == Constants.PersonNumberType.Real.value) {
            title = 'کد ملی';
            length = 10;
        }
        else if (e.value == Constants.PersonNumberType.Legal.value) {
            title = 'شناسه ملی';
            length = 11;
        }
        else if (e.value == Constants.PersonNumberType.Foreign.value) {
            title = 'کد فراگیر اتباع';
            length = 12;
        }
        this.setState({
            numberTitle: title,
            numberMaxLength: length,
            numberIsReadOnly: false,
        });
    };

    searchStr()
    {
        var personNumberTypeId = this.state.personNumberTypeId;
        var number = this.state.number;

        if(personNumberTypeId == null || personNumberTypeId == "" || number == null || number == "")
        {
            this.setState({ gridUpdate: this.state.gridUpdate + 1 });
            return;
        }
        if (personNumberTypeId == Constants.PersonNumberType.Real.value && !IsRealPersonNationalIdValid(number)) {
            Helpers.DisplayErrorMessage(this.notificationSystem, null, "کد ملی معتبر نیست");
            return;
        }
        if (personNumberTypeId == Constants.PersonNumberType.Legal.value && !IsLegalPersonNationalIdValid(number)) {
            Helpers.DisplayErrorMessage(this.notificationSystem, null, "شناسه ملی معتبر نیست");
            return;
        }
        this.setState({ gridUpdate: this.state.gridUpdate + 1 });
    }

    returnValue(value)
    {
        return (value == null || value == "") ? "**" : value;
    }

    downLoadStr()
    {
        Helpers.CallServer('/api/amls/Request/RequestDownload_STR_Report',
            {
                fromTransactionDate: this.state.fromTransactionDate,
                toTransactionDate: this.state.toTransactionDate,
                customerNumber: this.state.customerNumber,
                accountNumber: this.state.accountNumber,
                personNumberTypeId: this.state.personNumberTypeId,
                number: this.state.number,
                fromAmount: this.state.fromAmount,
                toAmount: this.state.toAmount
            },
            successfulResponse =>
            {
                Helpers.DisplaySuccessMessage(this.notificationSystem, successfulResponse.message);
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) });
    }

    resetFilters()
    {
        this.setState({
            fromTransactionDate: null,
            toTransactionDate: null,
            customerNumber: null,
            accountNumber: null,
            personNumberTypeId: null,
            number: null,
            fromAmount: null,
            toAmount: null,
            filterUpdate: this.state.filterUpdate + 1
        });
    }

