        return (
            <>
                <PageMain context={ this } title="گزارش STR" icon="list-search" isLoading={ this.state.isLoading }
                    footerLinks={[{ href: "/", text: "صفحه اصلی" }]} key={ this.state.filterUpdate }
                    topButtons={[
                        {
                            title: "فهرست درخواست گزارش STR",
                            url: GetUrl("requestBranchStrListPage"),
                            color: "outline-primary",
                            icon: "list-search",
                        }
                    ]}>
