import { Component } from "react";
import React from "react";
import NotificationSystem from "react-notification-system";
import LegalCompanyLoanInfoSection from "../../../sections/projects/legalCompanyInfoSection";
import { PageMain, Helpers } from "core";
import { GetUrl } from '../../../utils/generals'

export default class LegalCompanyInfoEditPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      formData: null
    };

    this.notificationSystem = React.createRef();
    this.companyInfoId = this.props.match.params.companyInfoId;
    this.companyLoanAccountId = this.props.match.params.companyLoanAccountId;
  }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/FRQST/LegalCompanyInfo/GetCompanyInfo',
      {
        LegalCompanyInfoId: this.companyInfoId,
        CompanyLoanAccountId: this.companyLoanAccountId
      },
      successfulResponse => this.setState({ formData: successfulResponse.data }),
      errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
      () => this.setState({ isLoading: false }))
  }

  render() {
    return (
        <>
            <PageMain context={ this } title="ویرایش اطلاعات شرکت" icon="building-community" isLoading={ this.state.isLoading }
              topButtons={[
                {
                  title: "فهرست شرکت ها",
                  url: GetUrl("legalCompanyInfoListPage"),
                  color: "outline-success",
                  icon: "list",
                }]}
            footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>
              {
                (this.state.formData == null
                  ? null
                  : <LegalCompanyLoanInfoSection formData={ this.state.formData.legalCompanyInfo } personnelBranchInfo={ this.state.formData } />)
              }
            </PageMain>
            <NotificationSystem ref={ this.notificationSystem } />
        </>
        );
    }
}
