import React from "react";
import { PageMain, SimpleGrid } from "core";
import NotificationSystem from 'react-notification-system'
import { GetUrl, HandleError } from '../../utils/generals'

export default class PersonelRegionListPage extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = {};
        this.notificationSystem = React.createRef();

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

    // Events.
    onDeleteErrorFunc()
    {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
    }

    render()
    {
        return <>
                <PageMain
                    title='فهرست پرسنل / منطقه'
                    icon="users"
                    context={ this }
                    footerLinks={[
                    { href: "/", text: "صفحه اصلی" },
                    { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" },
                    ]}>
                        <SimpleGrid context={ this }
                            name="grid"
                            title="جزئیات پرسنل / منطقه"
                            enableAutoFetch
                            listApiUrl='/api/FRQST/PersonelRegion/GetPersonelRegionList'
                            filterOptions={ [
                                    { type: 'auto-complete', name: 'personelCode', title: 'پرسنل' , lookupApiUrl:'/api/FRQST/PersonelRegion/SearchPersonels' },
                                    { type: 'auto-complete', name: 'regionCode', title: 'منطقه' , lookupApiUrl:'/api/FRQST/PersonelRegion/SearchRegions' } ]}
                            headers={[
                                        { title: "کد پرسنل", name: "personelCode" },
                                        { title: "نام پرسنل", name: "personelName" },
                                        { title: "کد منطقه", name: "regionCode" },
                                        { title: "نام منطقه", name: "regionName" }
                                    ]}
                            hideExportButton
                            minHeightTableWrapper={ 200 }
                            editPageUrl={ GetUrl('personelRegionEdit') }
                            deleteApiUrl='/api/FRQST/PersonelRegion/DeletePersonelRegion'
                            deleteModalFieldNames={[ "personelCode", "personelName", "regionCode", "regionName" ]}
                            onDeleteErrorFunc={ this.onDeleteErrorFunc }
                            //moreInfoLinks={[{ title:'نمایش', icon:'eye', url: GetUrl('closingRequestView/#replaceWith_id#/#replaceWith_name#') }]}
                            notificationSystem={ this.notificationSystem } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>;
    }
}
