import { Component } from "react";
import React from "react";
import { Card, DropDown, Helpers, DatePicker, TextBox } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"

const defaultName = 'نام';
const defaultCode = 'کد';

export default class PersonnelInfSecion extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            ctlName: defaultName,
            ctlCode: defaultCode
        };

        this.notificationSystem = React.createRef();

        this.onPersonTypeSelected = this.onPersonTypeSelected.bind(this);
        this.onPersonnelNationalitySelected = this.onPersonnelNationalitySelected.bind(this);
    }

    setPersonTypeLables(value)
    {
        var name = defaultName;
        var code = defaultCode;

        switch(value)
        {
            case "1":
                name = "نام و نام خانوادگی";
                code = "کد ملی";
            break;
            case "2":
                name = "نام شرکت";
                code = "شناسه ملی";
            break;
        }

        return {
            name: name,
            code: code
        }
    }

    // Events.
    onPersonTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({
                ctlName: defaultName,
                ctlCode: defaultCode
            });
            return;
        }
        var value = item.value.toString();
        var personnelNationalityId = this.state.personnelNationalityId;
        var nameCode = {
            name: defaultName,
            code: defaultCode
        };
        var name = defaultName;
        var code = defaultCode;

        if(personnelNationalityId == "1")
        {
            nameCode = setPersonTypeLables(value);
            // switch(value)
            // {
            //     case "1":
            //         name = "نام و نام خانوادگی";
            //         code = "کد ملی";
            //     break;
            //     case "2":
            //         name = "نام شرکت";
            //         code = "شناسه ملی";
            //     break;
            // }
        }
        else if(personnelNationalityId == "2")
        {
            name = "نام و نام خانوادگی";
            code = "کد فراگیر اتباع";
        }
        this.setState({
            ctlName: name,
            ctlCode: code
        });
    }

    onPersonnelNationalitySelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({
                ctlName: defaultName,
                ctlCode: defaultCode
            });
            return;
        }
        var value = item.value.toString();
        var name = defaultName;
        var code = defaultCode;

        switch(value)
        {
            case "1":

            break;
            case "2":

            break;
        }
        this.setState({
            ctlName: name,
            ctlCode: code
        });
    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن شخص" : "جزئیات ویرایش شخص" } name="crdPersonnelInfo" statusColor="primary"
                    hasDefaultSaveButton submitUrl={ this.props.record == null ? "/api/amls//" : "/api/amls//" }
                    hasBackButton icon="users" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} onSubmitSuccess={ this.onSubmitSuccess }
                    updateKey={ this.state.updateKey }>

                    <DropDown context={ this } name="statusId" title="وضعیت شخص" options={ this.props.dropDowns.states } required />
                    <DropDown context={ this } name="personnelTypeId" title="نوع شخص" options={ this.props.dropDowns.personTypes } required changeHandler={ this.onPersonTypeSelected } />
                    <DropDown context={ this } name="personnelNationalityId" title="تابعیت" options={ this.props.dropDowns.personNationalities } required changeHandler={ this.onPersonnelNationalitySelected } />
                    <DatePicker context={ this } name="expirationDateFrom" title="تاریخ اعتبار از" useEpoch required />
                    <TextBox context={ this } name="name" title={ this.state.ctlName } minLength={ 1 } maxLength={ 64 } required validateHandler={[ Helpers.MinMaxLengthValidator ]}  />
                    <TextBox context={ this } name="code" title={ this.state.ctlCode } minLength={ 11 } maxLength={ 12 } required validateHandler={[ Helpers.MinMaxLengthValidator ]}  />
                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
