import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from 'react-notification-system';
import { GetUrl, HandleError } from '../../utils/generals'


export default class DropDownListPage extends React.Component
{
    // Constructor
    constructor(props)
    {
        super(props);
        this.notificationSystem = React.createRef();
        this.state = { DropDown: null };

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

    onDeleteErrorFunc(errorResponse)
    {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
    }

    render()
    {
        return (<>
                    <PageMain
                        title="فهرست داده های لیستی"
                        icon="list-details"
                        context={ this }
                        footerLinks={[
                        { href: "/", text: "صفحه اصلی" },
                        { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" },
                        ]}>
                            <SimpleGrid context={ this }
                                name="grid"
                                title="جزئیات داده های لیستی"
                                enableAutoFetch
                                notificationSystem={ this.notificationSystem }
                                listApiUrl='/api/FRQST/DropDown/GetDropDownList'
                                editPageUrl={ GetUrl('dropDownEditPage') }
                                deleteApiUrl='/api/FRQST/DropDown/Delete'
                                deleteModalFieldNames={["name"]}
                                onDeleteErrorFunc={ this.onDeleteErrorFunc }
                                dataKey="id"
                                headers={[{ title: "نام داداه شمارشی ", name: "name" }]}
                                hideExportButton
                                disabledPaging
                                minHeightTableWrapper={ 200 } />
                    </PageMain>
                    <NotificationSystem ref={ this.notificationSystem } />
                </>)
    }
}
