import { Helpers, PageMain, SimpleGrid } from "core";
import React, { Component } from "react";
import NotificationSystem from "react-notification-system";
import { IsLegalPersonNationalIdValid, IsRealPersonNationalIdValid } from "../../../utils/nationalIdentification";
import Constants from "../../../utils/constants"

export default class RequestStrListPage extends Component {
    constructor(props) {
        super(props);

        this.PersonTypes = [ Constants.PersonNumberType.Real, Constants.PersonNumberType.Legal, Constants.PersonNumberType.Foreign ];

        this.state = {
            isLoading: false,
            filterOptions: [
                { type: 'datepicker', name: 'transactionFromDate', title: 'از تاریخ تراکنش', useEpoch: true, isRequired: true },
                { type: 'datepicker', name: 'transactionToDate', title: 'تا تاریخ تراکنش', useEpoch: true, isRequired: true },
                { type: 'number', name: 'customerNumber', title: 'شماره مشتری', minLength: '1', maxLength: '25', placeholder: ' ' },
                { type: 'number', name: 'accountNumber', title: 'شماره حساب', minLength: '1', maxLength: '25', placeholder: ' ' },
                { type: 'select', name: 'personNumberTypeId', title: 'نوع شخص', selectOptions: this.PersonTypes, isRequired: true },
                { type: 'text', name: 'nationalNumber', title: 'کد ملی/ شناسه ملی/کد فراگیر اتباع', isRequired: true },
            ],
        };

        this.notificationSystem = React.createRef();
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
    }

    onBeforeSubmit(e) {
        return true;
        if (e.filter.personNumberTypeId == Constants.PersonNumberType.Real.value && !IsRealPersonNationalIdValid(e.filter.nationalNumber)) {
            //Helpers.DisplayErrorMessage(this.notificationSystem, Constants.ValidationMessages.RealNationalNumberIsNotValid);
            return false;
        }
        if (e.filter.personNumberTypeId == Constants.PersonNumberType.Legal.value && !IsLegalPersonNationalIdValid(e.filter.nationalNumber)) {
            //Helpers.DisplayErrorMessage(this.notificationSystem, Constants.ValidationMessages.LegalNationalNumberIsNotValid);
            return false;
        }
        return true;
    }

    componentDidMount() {
        document.getElementById('input-filter_nationalNumber').setAttribute('disabled', true);
        document.getElementById('input-filter_customerNumber').setAttribute('disabled', true);
        this.state.filterOptions.filter(f => f.name == 'personNumberTypeId')[0].onChange = this.onPersonNumberTypeIdChange;
    }

    onPersonNumberTypeIdChange = (e) => {
        let title = 'کد ملی/ شناسه ملی/کد فراگیر اتباع';
        let length = 0;

        if (e.value == Constants.PersonNumberType.Real.value) {
            title = 'کد ملی';
            length = 10;
        }
        else if (e.value == Constants.PersonNumberType.Legal.value) {
            title = 'شناسه ملی';
            length = 11;
        }
        else if (e.value == Constants.PersonNumberType.Foreign.value) {
            title = 'کد فراگیر اتباع';
            length = 12;
        }
        this.state.filterOptions[5] = { type: 'text', name: 'nationalNumber', title: title, minLength: length, maxLength: length, placeholder: ' ' };
        document.getElementById('input-filter_nationalNumber').removeAttribute('disabled');
    };

    render() {
        return (
            <>
                <PageMain context={ this } title="گزارش STR" icon="report-search" isLoading={ this.state.isLoading }
                    footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>
                    <div className='card'>
                        <div className='card-body'>
                            <SimpleGrid listApiUrl="/api/amls/bi/Receive_STR_/SearchReceive_STR" context={ this } title="گزارش STR" dataKey="id"
                                name="grSrtReport" lgCol={ 12 } mdCol={ 12 } notificationSystem={ this.notificationSystem }
                                minHeightTableWrapper={ Constants.MinGridHeight } onBeforeSubmit={ this.onBeforeSubmit } filterOptions={ this.state.filterOptions }
                                headers={[
                                    //{ type: "text", title: "شناسه", name: "Id" },
                                    { type: "text", title: "نام بانک", name: "bankName" },
                                    { type: "text", title: "نام شعبه افتتاح  کننده", name: "openingBranchName" },
                                    { type: "text", title: "کد شعبه افتتاح کننده", name: "openingBranchCode" },
                                    { type: "text", title: "نام استان شعبه", name: "branchProvinceName" },
                                    { type: "text", title: "نام شهر شعبه", name: "branchCityName" },
                                    { type: "text", title: "تاریخ ترکنش", name: "transactionDate" },
                                    { type: "text", title: "نام صاحب حساب", name: "ownerAccountFirstName" },
                                    { type: "text", title: "نام خانوادگی صاحب حساب", name: "ownerAccountLastName" },
                                    { type: "text", title: "کد ملی صاحب حساب", name: "ownerAccountNationalCode" },
                                    { type: "text", title: "شماره شناسنامه صاحب حساب", name: "ownerAccountBirthCertificateNumber" },
                                    { type: "text", title: "نام پدر صاحب حساب", name: "ownerAccountFatherName" },
                                    { type: "text", title: "نوع تراکنش", name: "transactionType" },
                                    { type: "text", title: "نوع وجه", name: "currencyType" },
                                    { type: "text", title: "نوع حساب", name: "accountTypeName" },
                                    { type: "text", title: "شماره حساب", name: "accountNumber" },
                                    { type: "text", title: "نام بانک طرف حساب", name: "accountBanckName" },
                                    { type: "text", title: "مبلغ", name: "amount" },
                                    { type: "text", title: "ارز", name: "currency" },
                                    { type: "text", title: "تاریخ چک", name: "checkDate" },
                                    { type: "text", title: "سری چک", name: "checkSeries" },
                                    { type: "text", title: "شماره سریال چک", name: "checkSerialNumber" },
                                    { type: "text", title: "نوع چک", name: "checkType" },
                                    { type: "text", title: "نام بانک چک", name: "checkBankName" },
                                    { type: "text", title: "نام شعبه چک", name: "checkBranchName" },
                                    { type: "text", title: "کد شعبه چک", name: "checkBranchCode" },
                                    { type: "text", title: "شماره حساب جاری", name: "currentAccountNumber" },
                                    { type: "text", title: "نام چک", name: "checkName" },
                                    { type: "text", title: "مبلغ چک", name: "checkAmount" },
                                    { type: "text", title: "نوع ارز چک", name: "checkCurrencyType" },
                                    { type: "text", title: "پرداخت کننده چک", name: "checkPayer" },
                                    { type: "text", title: "امضا کننده چک", name: "checkSigner" },
                                    { type: "text", title: "گیرنده چک", name: "checkBuyer" },
                                    { type: "text", title: "نام", name: "firstName" },
                                    { type: "text", title: "نام خانوادگی", name: "lastName" },
                                    { type: "text", title: "نام پدر", name: "fatherName" },
                                    { type: "text", title: "شناسه ملی", name: "nationalCode" },
                                    { type: "text", title: "شمار شناسنامه", name: "birthCertificateNumber" },
                                    { type: "text", title: "تاریخ تولد", name: "birthDate" },
                                    { type: "text", title: "استان", name: "provinceName" },
                                    { type: "text", title: "شهر", name: "cityName" },
                                    { type: "text", title: "آدرس", name: "address" },
                                    { type: "text", title: "کد پستی", name: "postalCode" },
                                    { type: "text", title: "تلفن", name: "telephoneNumber" },
                                    { type: "text", title: "موبایل", name: "cellphneNumber" },
                                    { type: "text", title: "شغل", name: "jobName" },
                                    { type: "text", title: "آدرس محل کار", name: "jobAddress" },
                                    { type: "text", title: "کد پستی محل کار", name: "jobPostalCode" },
                                    { type: "text", title: "تلفن محل کار", name: "jobTelephoneNumber" },
                                    { type: "text", title: "توضیحات", name: "describtion" },
                                    { type: "text", title: "شماره STR", name: "sTR_Number" }
                                ]}
                            />
                        </div>
                        <div className="card-footer">
                            <div className="d-flex">
                                <a className="btn btn-outline-secendery ms-auto" onClick={(e) => this.Backward(e)}
                                    href="/" title="بازگشت به صفحه قبل">بازگشت<i className="ti ti-arrow-left ms-1"></i></a>
                            </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
