import React from "react";
import { PageMain, Helpers } from "core";
import PersonelRegionSection from "../../sections/personelRegionSection";
import { GetUrl, HandleError } from "../../utils/generals";
import NotificationSystem from 'react-notification-system'

export default class PersonelRegionEditPage extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            isLoading: true
        };
        this.notificationSystem = React.createRef();

        this.dropDownId = this.props.match.params.personelRegionId;
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/FRQST/PersonelRegion/GetPersonelRegion',
        { Id: this.dropDownId },
        successfulResponse =>
        {
            this.setState({ PersonRegionData: successfulResponse.data });
        },
        errorResponse => {
            return HandleError(Helpers, this.notificationSystem, errorResponse);
        },
        () =>
        {
            this.setState({ isLoading: false });
        })
    }

    render()
    {
        return (
            <>
                <PageMain
                        isLoading={ this.state.isLoading }
                        title="ویرایش پرسنل / منطقه"
                        icon="user-check"
                        context={ this }
                        topButtons={[{ title: "فهرست پرسنل / منطقه", icon: "users", color: "outline-success", url: GetUrl("personelRegionList") } ]}
                        footerLinks={[{ href: "/", text: "صفحه اصلی" },{ href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" }]}>
                    {
                        this.state.PersonRegionData != null && <PersonelRegionSection personRegionData={ this.state.PersonRegionData } />
                    }
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
