import { Card, CheckBox, DropDown, Helpers, TextBox, FileUploader } from "core";
import React, { Component } from "react";
import NotificationSystem from "react-notification-system";
import Constants from "../utils/constants";
import { HandleError } from "../utils/generals";
import { IsLegalPersonNationalIdValid, IsRealPersonNationalIdValid } from '../utils/nationalIdentification';

const defaultName = 'نام';
const defaultCode = 'کد';
const NationalityTypes =
{
    Iranian: "1",
    OtherCountry: "2"
};

export default class PersonInfSecion extends Component
{
    constructor(props)
    {
        var state = {
            ctlName: defaultName,
            ctlCode: defaultCode,
            isActive: false,
            updateKey: 1
        };

        super(props);

        if(this.props.record != null)
        {
            state["id"] = this.props.record.id;
            state["statusId"] = this.props.record.statusId.toString();
            state["personTypeId"] = this.props.record.personTypeId.toString();
            state["personNationalityId"] = this.props.record.personNationalityId.toString();
            state["isActive"] = this.props.record.isActive;
            state["name"] = this.props.record.name;
            state["code"] = this.props.record.code;
        }
        this.state = state;

        this.notificationSystem = React.createRef();

        this.onPersonTypeSelected = this.onPersonTypeSelected.bind(this);
        this.onPersonnelNationalitySelected = this.onPersonnelNationalitySelected.bind(this);
        this.setNationalityLabels = this.setNationalityLabels.bind(this);
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
    }

    setPersonTypeLables(value)
    {
        var name = defaultName;
        var code = defaultCode;

        switch(value)
        {
            case Constants.PersonTypes.Real:
                name = "نام و نام خانوادگی";
                code = "کد ملی";
            break;
            case Constants.PersonTypes.Legal:
                name = "نام شرکت";
                code = "شناسه ملی";
            break;
        }

        return {
            name: name,
            code: code
        }
    }

    setNationalityLabels(value)
    {
        var name = defaultName;
        var code = defaultCode;

        if(value == NationalityTypes.Iranian)
            return this.setPersonTypeLables(this.state.personnelTypeId);
        else if(value == NationalityTypes.OtherCountry)
        {
            name = "نام شرکت به انگلیسی";
            code = "کد فراگیر اتباع";
        }

        return {
            name: name,
            code: code
        }
    }

    // Events.
    onPersonTypeSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({
                ctlName: defaultName,
                ctlCode: defaultCode
            });
            return;
        }
        var value = item.value.toString();
        var personnelNationalityId = this.state.personnelNationalityId;
        var nameCode = {
            name: defaultName,
            code: defaultCode
        };

        if(personnelNationalityId == NationalityTypes.Iranian || personnelNationalityId == null)
            nameCode = this.setPersonTypeLables(value);
        else if(personnelNationalityId == NationalityTypes.OtherCountry)
            nameCode = this.setNationalityLabels(personnelNationalityId);

        this.setState({
            ctlName: nameCode.name,
            ctlCode: nameCode.code
        });
    }

    onPersonnelNationalitySelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({
                ctlName: defaultName,
                ctlCode: defaultCode
            });
            return;
        }
        var value = item.value.toString();
        var nameCode = {
            name: defaultName,
            code: defaultCode
        };

        if(value == NationalityTypes.Iranian)
            nameCode = this.setPersonTypeLables(this.state.personnelTypeId);
        else if(value == NationalityTypes.OtherCountry)
            nameCode = this.setNationalityLabels(value);

        this.setState({
            ctlName: nameCode.name,
            ctlCode: nameCode.code
        });
    }

    onBeforeSubmit()
    {
        var personnelNationalityId = this.state.personnelNationalityId;

        if(personnelNationalityId == NationalityTypes.Iranian)
        {
            var personnelTypeId = this.state.personnelTypeId;

            if(personnelTypeId == Constants.PersonTypes.Real)
            {
                var isVlid = IsRealPersonNationalIdValid(this.state.code);

                if(isVlid == false)
                {
                    Helpers.DisplayErrorMessage(this.notificationSystem, null, 'کد ملی صحیح نمی باشد');
                    return false;
                }
            }
            else if(personnelTypeId == Constants.PersonTypes.Legal)
            {
                var isVlid = IsLegalPersonNationalIdValid(this.state.code);

                if(isVlid == false)
                {
                    Helpers.DisplayErrorMessage(this.notificationSystem, null, 'شناسه ملی صحیح نمی باشد');
                    return false;
                }
            }
        }
        return true;
    }

    onSubmitSuccess(response)
    {
        if(this.props.record == null)
            this.setState({
                statusId: null,
                personTypeId: null,
                personNationalityId: null,
                isActive: false,
                name: null,
                code: null,
                updateKey: this.state.updateKey + 1
            });
        Helpers.DisplaySuccessMessage(this.notificationSystem, response.message);
    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن شخص" : "جزئیات ویرایش شخص" } name="crdPersonnelInfo" statusColor="primary"
                    hasDefaultSaveButton submitUrl={ this.props.record == null ? "/api/amls/PersonInfo/AddPerson" : "/api/amls/PersonInfo/UpdatePerson" }
                    hasBackButton icon={ this.props.record == null ? "user-plus" : "user-check" } onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}
                    onBeforeSubmit={ this.onBeforeSubmit } onSubmitSuccess={ this.onSubmitSuccess } updateKey={ this.state.updateKey }>

                    <DropDown context={ this } name="statusId" title="وضعیت شخص" options={ this.props.dropDowns.states } required />
                    <DropDown context={ this } name="personTypeId" title="نوع مشتری" options={ this.props.dropDowns.personTypes } required
                        changeHandler={ this.onPersonTypeSelected } disabled={ this.state.personNationalityId == "2" ? true : false }/>
                    <DropDown context={ this } name="personNationalityId" title="تابعیت" options={ this.props.dropDowns.personNationalities } required changeHandler={ this.onPersonnelNationalitySelected } />
                    <TextBox context={ this } name="name" title={ this.state.ctlName } minLength={ 1 } maxLength={ 64 } required validateHandler={[ Helpers.MinMaxLengthValidator ]}  />
                    <TextBox context={ this } name="code" title={ this.state.ctlCode } minLength={ 10 } maxLength={ 12 } required
                        validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]}  />
                    <CheckBox context={ this } name="isActive" title="فعال" required  />
                    {
                        this.props.record == null
                            ? <FileUploader context={this} name="fulPersonInfo" title="ایمورت فایل" validExtensions={['.xlsx']} />
                            : null
                    }
                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
