import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid  } from "core";
import NotificationSystem from "react-notification-system";
import Constants from "../../../utils/constants";
import { GetUrl } from "../../../utils/generals";

export default class LegalCompanyInfoListPage extends Component {
  constructor(props) {
    super(props);
    this.state = { isLoading: false };

    this.notificationSystem = React.createRef();
  }

  // Events.
  render() {
    return (
      <>
        <PageMain
          title='فهرست شرکت ها'
          icon='list'
          isLoading={ this.state.isLoading }
          topButtons={[
            {
              title: "راهنمای استفاده از سایت",
              url: GetUrl("downlaodFile"),
              color: "outline-warning",
              icon: "help",
            },
            {
              title: "افزودن اطلاعات شرکت",
              url: GetUrl("legalCompanyInfoAddPage"),
              color: "outline-success",
              icon: "building-community",
            },
          ]}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>
          <SimpleGrid context={ this } dataKey="companyLoanAccountId" minHeightTableWrapper={ Constants.MinGridHeight} filterBgColor="primary" name="grCompanyInfo"
            title="جزئیات فهرست شرکت ها " notificationSystem={ this.notificationSystem } enableAutoFetch
            listApiUrl="/api/FRQST/LegalCompanyInfo/SearchCompanies"
            headers={[
              { title: "نام شرکت", name: "companyName" },
              { title: "شماره مشتری", name: "customerNumber" },
              { title: "شماره ملی", name: "nationalId" },
              { title: "شماره حساب", name: "accountNumber" },
              { title: "شماره تفاهم نامه", name: "contractId" },
              { title: "نام شعبه", name: "branchName" },
              { title: "کد شعبه", name: "branchCode" },
              { title: "کد منطقه", name: "regionCode" },
              { title: "دریافت حقوق از سپه", name: "isSalaryPaymentOnSepahBank", type: 'bool' },
              { title: "تاریخ ثبت تفاهم نامه", name: "registerDateStr" },
              { title: "تاریخ افتتاح حساب", name: "accountOpeningDateStr" },
            ]}
            moreInfoLinks={[
              { title:'ویرایش', icon:'building-community', url: GetUrl('legalCompanyInfoEditPage/#replaceWith_legalCompanyInfoId#/#replaceWith_companyLoanAccountId#') },
              { title:'ثبت تفاهم نامه', icon:'report-money', url: GetUrl('companyLoanInfoPage/#replaceWith_legalCompanyInfoId#/#replaceWith_companyLoanAccountId#') },
              { title:'جزئیات تفاهم نامه', icon:'report-money', url: GetUrl('companyDetailLoanInfoPage/#replaceWith_legalCompanyInfoId#/#replaceWith_companyLoanAccountId#') },
              { title:'ثبت میانگین مانده حساب', icon:'file-dollar', url: GetUrl('averageCompanyAccountBalancePage/#replaceWith_legalCompanyInfoId#/#replaceWith_companyLoanAccountId#') },
              { title:'ریز تسهیلات پرداختی', icon:'cash-banknote', url: GetUrl('paiedLoanListPage/#replaceWith_legalCompanyInfoId#/#replaceWith_companyLoanAccountId#') }
            ]}
            filterOptions={[
              { type: "text", name: "companyName", title: "نام شرکت" },
              { type: "text", name: "customerNumber", title: "شماره مشتری" },
              { type: "text", name: "nationalId", title: "شماره ملی" },
              { type: "text", name: "accountNumber", title: "شماره حساب " },
              { type: "text", name: "contractId", title: "شماره تفاهم نامه " }
            ]}
          />
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
