import React from 'react';
import { PageMain, Helpers } from "core";
import NotificationSystem from 'react-notification-system';
import FieldsSection from '../../sections/basePage/fieldsSection';
import { HandleError } from "../../utils/generals";

export default class FieldsSavePage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            FormFieldsInfo: null,
            isLoading: true
        }
        this.notificationSystem = React.createRef();
        this.formId = this.props.match.params.formId;
    }

    // Events
    componentDidMount()
    {
        if(this.formId == null)
            return;
        Helpers.CallServer('/api/FRQST/Field/GetFormFieldsInfo',
        { Id: this.formId },
        successfulResponse =>
        {
            this.setState({ FormFieldsInfo: successfulResponse.data });
        },
        errorResponse => {
            return HandleError(Helpers, this.notificationSystem, errorResponse);
        },
        () =>
        {
            this.setState({ isLoading: false });
        });
    }

    render() {
        return (this.formId == null || this.state.FormFieldsInfo != null) && (
            <>
                <PageMain title="ثبت اطلاعات فیلد"
                        icon="forms"
                        isLoading={ this.state.isLoading }
                        context={ this }
                        footerLinks={[{ href: "/", text: "صفحه اصلی" }, { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" }]}>
                    <FieldsSection formFieldsInfo={ this.state.FormFieldsInfo }/>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
